INSERT INTO `flc_bl` VALUES ('10', 'KEMASUKAN_BANK_SOALAN_SINGLE', 'KEMASUKAN_BANK_SOALAN_SINGLE', '', 'include(\'db.php\');\r\n\r\n$kod_tahap_soalan = $_POST[\'input_map_127_8671\'];\r\n$kod_kategori_soalan = $_POST[\'input_map_127_5403\'];\r\n$kod_jenis_soalan = $_POST[\'input_map_127_5404\'];\r\n$pengetahuan_soalan = $_POST[\'input_map_127_936\'];\r\n$topik_soalan= $_POST[\'input_map_127_937\'];\r\n$arahan_soalan= $_POST[\'input_map_127_938\'];\r\n$penyataan_soalan= $_POST[\'input_map_128_940\'];\r\n$id_soalan= $_POST[\'input_map_127_4630\'];\r\n$id_pengguna = $_POST[\'input_map_127_5092\'];\r\n$upload=upload_file(\'./upload/\',$_FILES[\'input_map_144_8668\']);\r\n\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n$sql = \"insert into usr_isac.pro_soalan (KOD_TAHAP_SOALAN,KOD_KATEGORI_SOALAN,MUAT_NAIK_FAIL,ARAHAN_SOALAN,PENYATAAN_SOALAN,KOD_STATUS,TARIKH_CIPTA,ID_PENGGUNA) values\r\n(\'$kod_tahap_soalan\',\'$kod_kategori_soalan\',\'$upload\',\'$arahan_soalan\',\'$penyataan_soalan\',\'02\',\'$tarikh\',\'$id_pengguna\')\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"insert into usr_isac.pro_pengetahuan (ID_SOALAN,KOD_JENIS_SOALAN,PENGETAHUAN_SOALAN,TOPIK_SOALAN) values\r\n((select max(id_soalan) from usr_isac.pro_soalan),\'$kod_jenis_soalan\',\'$pengetahuan_soalan\',\'$topik_soalan\')\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n$j = $_POST[\'input_map_130_952\'];\r\n$jwpn=$_POST[\'input_map_130_941\'];\r\nfor($x = 0; $x < count($jwpn); $x++)\r\n{\r\n\r\nif($jwpn[$x])\r\n{\r\n	$sql3 = \"insert into usr_isac.pro_pilihan_jawapan (id_soalan,keterangan_jawapan) values\r\n		((select max(id_soalan) from usr_isac.pro_soalan),\'$jwpn[$x]\')\";\r\n	$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n	//if order of chkbox same as row of jwpn\r\n	if($result3&&$j[0]==$x)\r\n	{\r\n		$sql4 = \"insert into usr_isac.pro_jawapan (id_soalan,id_pilihan_jawapan) values\r\n			((select max(id_soalan) from usr_isac.pro_soalan),(select max(id_pilihan_jawapan)from usr_isac.pro_pilihan_jawapan))\";\r\n		$result4 = $myQuery->query($sql4,\'RUN\');\r\n	}\r\n}\r\n}\r\n\r\nif($result && $result2)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n}\r\n', '', '', '00', '1', '2009-10-12', '1', '2009-12-21');
INSERT INTO `flc_bl` VALUES ('11', 'KEMASKINI_PEMILIHAN_SET_SOALAN', 'Kemaskini_Pemilihan_Set_Soalan', '', 'include(\'db.php\');\r\n\r\n$nama_pemilihan_soalan = $_POST[\'input_map_167_1052\'];\r\n$tahap = $_POST[\'input_map_167_4639\'];\r\n$jumlah_keseluruhan_soalan = $_POST[\'input_map_167_1054\'];\r\n$nilai_jumlah_markah = $_POST[\'input_map_167_1055\'];\r\n$nilai_markah_lulus = $_POST[\'input_map_167_1053\'];\r\n$id_pemilihan_soalan = $_POST[\'input_map_167_9182\'];\r\n\r\n$id_pemilihan_soalan_kumpulan = $_POST[\'input_map_168_9177\'];\r\n$kod_kategori_soalan = $_POST[\'input_map_168_1057\'];\r\n$nilai_jumlah_soalan = $_POST[\'input_map_168_1058\'];\r\n$kod_tahap_soalan1 = $_POST[\'input_map_168_1056\'];\r\n//$result_check = $_POST[\'input_map_168_9177\'];\r\n\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh_kemaskini=date(\"Y-m-d\",$date1);\r\n\r\n$sql = \"update usr_isac.pro_pemilihan_soalan set\r\nnama_pemilihan_soalan=\'$nama_pemilihan_soalan\',kod_tahap_soalan=\'$tahap\',nilai_markah_lulus=\'$nilai_markah_lulus\', nilai_jumlah_markah=\'$nilai_jumlah_markah\', jumlah_keseluruhan_soalan=\'$jumlah_keseluruhan_soalan\', tarikh_kemaskini=\'$tarikh_kemaskini\' where id_pemilihan_soalan =\'$id_pemilihan_soalan\'\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\nfor($x = 0; $x < count($_POST[\'input_map_168_9177\']); $x++)\r\n{\r\n\r\n\r\nif($tahap==\'01\')\r\n{\r\n$kod_tahap_soalan1[$x] = \'01\';\r\n}\r\n\r\n$id_pemilihan_soalan_kumpulan = $_POST[\'input_map_168_9177\'][$x];\r\n$kod_kategori_soalan = $_POST[\'input_map_168_1057\'][$x];\r\n$nilai_jumlah_soalan = $_POST[\'input_map_168_1058\'][$x];\r\n$kod_tahap_soalan1 = $_POST[\'input_map_168_1056\'][$x];\r\n\r\n//check if id_pemilihan_soalan_kumpulan already exist\r\nif (($id_pemilihan_soalan_kumpulan == \'\') && ($kod_tahap_soalan1 != \'\') && ($kod_kategori_soalan != \'\') && ($nilai_jumlah_soalan != \'\'))\r\n{\r\n$sql3 =  \"insert into usr_isac.pro_pemilihan_soalan_kumpulan (id_pemilihan_soalan,kod_tahap_soalan,kod_kategori_soalan,nilai_jumlah_soalan) values\r\n(\'$id_pemilihan_soalan\',\'$kod_tahap_soalan1\',\'$kod_kategori_soalan\',\'$nilai_jumlah_soalan\')\";\r\n$result3 = $myQuery->query ($sql3,\'RUN\');\r\n\r\n}\r\n\r\nelse if ($id_pemilihan_soalan_kumpulan)\r\n{\r\n$sql2 = \"update usr_isac.pro_pemilihan_soalan_kumpulan set kod_tahap_soalan=\'$kod_tahap_soalan1\',kod_kategori_soalan=\'$kod_kategori_soalan\',nilai_jumlah_soalan=\'$nilai_jumlah_soalan\' where id_pemilihan_soalan_kumpulan = \'$id_pemilihan_soalan_kumpulan\' \";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n}\r\n\r\n}\r\n\r\nif($result){\r\necho \"<div id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\n}\r\n\r\nelse{\r\necho \"<div id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}', '', '', '00', '1', '2009-10-14', '1', '2010-02-03');
INSERT INTO `flc_bl` VALUES ('12', 'HAPUS_DAFTAR_PENGGUNA', 'Hapus_Daftar_Pengguna', '', 'include(\'db.php\');\r\n\r\n$id = $_POST[\'input_map_3294_4897\'];\r\n$kumpulan_pengguna = $_POST[\'input_map_3294_4900\'];\r\n\r\n$sql = \"delete from PRUSER where userid =\'$id\'\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"delete from flc_user_group_mapping where user_id=\'$id\'\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n\r\n\r\n//penyelaras\r\n//if($kumpulan_pengguna == \'4\')\r\n//{\r\n\r\n$sql3 = \"delete from usr_isac.pro_penyelaras where userid=\'$id\' \";\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n\r\n//$sql4 = \"delete from usr_isac.pro_penyelaras a, usr_isac.pro_tempat_tugas b where a.id_penyelaras=b.id_penyelaras and a.userid=\'$id\' \";\r\n\r\n//$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\n//}\r\n\r\nif($result && $result2 && $result3){\r\n\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dipadam</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=108\');\r\n\r\n}\r\nelse{\r\n\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dipadam</div>\";\r\n\r\n}', '', '', '00', '1', '2009-10-19', '1', '2009-11-15');
INSERT INTO `flc_bl` VALUES ('13', 'DAFTAR_INDIVIDU', 'Daftar_Individu', '', 'include(\'db.php\');\r\n\r\n//prs permohonan\r\n$pilihan_sesi = $_POST[\'input_map_3284_4638\'];\r\n$kod_status_permohonan = $_POST[\'input_map_3282_8662\'];\r\n\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n//prs_permohonan_peserta\r\n$id_permohonan = $_POST[\'input_map_3282_4749\'];\r\n\r\n//syarat tiada kekosongan tempat\r\n$pilihan_sesi = $_POST[\'input_map_3284_4638\'];\r\n$tahap_penilaian=$_POST[\'input_map_3282_4755\'];\r\n\r\n//carian jadual penilaian\r\n$bulan = $_POST[\'input_map_3282_4636\'];\r\n$tahun = $_POST[\'input_map_3282_4637\'];\r\n\r\n$j = $_POST[\'input_map_3284_4638\'];\r\nfor($x = 0; $x < count($j); $x++)\r\n{\r\n\r\n//check bilangan kekosongan.\r\n$check=\"select bilangan_peserta from usr_isac.pro_sesi where id_sesi=\'$pilihan_sesi[$x]\' \";\r\n$checkRs=$myQuery->query($check,\'SELECT\');\r\n$empty = $checkRs[0][0];\r\n\r\nif ($empty == 0) \r\n{\r\n 	echo \"<script language=javascript>alert(\'Maaf, tiada kekosongan pada sesi ini,sila pilih sesi penilaian lain.\'); </script>\";\r\n	redirect(\'index.php?page=page_wrapper&menuID=34&tahap_penilaian=\'.$tahap_penilaian);\r\n}  \r\n\r\nelse\r\n{\r\n\r\n\r\n$peserta = \"select id_peserta from usr_isac.pro_peserta where (no_kad_pengenalan=\'{SESSION|publicID}\' or no_kad_pengenalan_lain=\'{SESSION|publicID}\') \"; \r\n$pesertaRs = $myQuery->query($peserta,\'SELECT\');\r\n$id_peserta = $pesertaRs[0][0];\r\n\r\n$permohonan = \"select id_permohonan from usr_isac.prs_permohonan where id_peserta=\'$id_peserta\' \"; \r\n$permohonanRs = $myQuery->query($permohonan,\'SELECT\');\r\n$id_permohonan = $permohonanRs[0][0];\r\n\r\n$per = \"select id_permohonan_peserta from usr_isac.prs_permohonan_peserta where id_peserta=\'$id_peserta\' \"; \r\n$perRs = $myQuery->query($per,\'SELECT\');\r\necho $id_per = $perRs[0][0];\r\n\r\nif ($id_per == \'\')\r\n{\r\n\r\n$sql5 = \"insert into usr_isac.prs_permohonan_peserta\r\n(id_peserta,id_permohonan,kod_status)\r\nvalues \r\n(\'$id_peserta\',\'$id_permohonan\',\'00\')\";\r\n$result5 = $myQuery->query($sql5,\'RUN\');\r\n\r\n}\r\n//echo $sql5;\r\n\r\n$j = $_POST[\'input_map_3284_4638\'];\r\nfor($x = 0; $x < count($j); $x++)\r\n{\r\n$sql4 = \"update usr_isac.prs_permohonan set kod_status_permohonan=\'$kod_status_permohonan\',kod_status_kehadiran=\'02\',tarikh_permohonan=\'$tarikh\',id_sesi=\'$pilihan_sesi[$x]\' where id_peserta=\'$id_peserta\'\";\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\n$sql6 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta-1) where id_sesi=\'$pilihan_sesi[$x]\'\";\r\n$result6= $myQuery->query($sql6,\'RUN\');\r\n\r\n}\r\n\r\n//------------- notification part-------------------------------------------------------\r\n$emel_peserta = $_POST[\'input_map_3282_4711\'];\r\n$emel_penyelia = $_POST[\'input_map_3282_4743\'];\r\n$no_kad_pengenalan = $_POST[\'input_map_3282_4687\'];\r\n$nama_peserta = $_POST[\'input_map_3282_4691\'];\r\n\r\n$sqlMaklumatSesi=\"SELECT \r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'SESI\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'TARIKH\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'MASA\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'PUSAT\',\r\n(SELECT description2 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'ALAMAT\',\r\na.LOKASI as \'LOKASI\'\r\n\r\nFROM usr_isac.pro_sesi a,usr_isac.prs_permohonan b,usr_isac.pro_peserta c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_peserta=c.id_peserta) \r\nAND (c.no_kad_pengenalan= \'\".$no_kad_pengenalan.\"\' OR\r\n c.no_kad_pengenalan_lain= \'\".$no_kad_pengenalan.\"\')\";\r\n\r\n$sqlMaklumatSesiRs = $myQuery->query($sqlMaklumatSesi,\'SELECT\',\'NAME\');\r\n$tempatPenilaian = $sqlMaklumatSesiRs[0][\"PUSAT\"];\r\n$masaPenilaian = $sqlMaklumatSesiRs[0][\"MASA\"];\r\n$tarikhPenilaian = $sqlMaklumatSesiRs[0][\"TARIKH\"];\r\n$sesiPenilaian = $sqlMaklumatSesiRs[0][\"SESI\"];\r\n$lokasi = $sqlMaklumatSesiRs[0][\"LOKASI\"];\r\n$alamat = $sqlMaklumatSesiRs[0][\"ALAMAT\"];\r\n\r\n$sqlTempoh = \"select emel_tawaran_penilaian_individu as \'TEMPOHTAWARAN\',\r\n				 emel_peringatan_sebelum_tarikh_penilaian as \'TEMPOHPERINGATAN\',\r\n				 tempoh_tutup_tarikh_penilaian_individu as \'TEMPOHTUTUP\'\r\n				 from usr_isac.pro_kawalan_sistem\";\r\n				 \r\n$sqlTempohRs= $myQuery->query($sqlTempoh,\'SELECT\',\'NAME\');\r\n$tempohTawaran = $sqlTempohRs[0][\"TEMPOHTAWARAN\"];\r\n$tempohPeringatan = $sqlTempohRs[0][\"TEMPOHPERINGATAN\"];\r\n$tempohJadualSemula = $sqlTempohRs[0][\"TEMPOHTUTUP\"];\r\n\r\n\r\n$sqlDateB4=\"SELECT \r\ndate_add(date_format(a.tarikh_sesi,\'%Y-%m-%d\'), interval -\'\".$tempohTawaran.\"\' day) AS \'TARIKHTAWARAN\',\r\ndate_add(date_format(a.tarikh_sesi,\'%Y-%m-%d\'), interval -\'\".$tempohPeringatan.\"\' day) AS \'TARIKHPERINGATAN\',\r\ndate_add(a.tarikh_sesi, interval -\'\".$tempohJadualSemula.\"\' day) AS \'TARIKHAKHIRPENJADUALAN\'\r\nFROM usr_isac.pro_sesi a,usr_isac.prs_permohonan b,usr_isac.pro_peserta c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_peserta=c.id_peserta) \r\nAND (c.no_kad_pengenalan= \'\".$no_kad_pengenalan.\"\' OR\r\n c.no_kad_pengenalan_lain= \'\".$no_kad_pengenalan.\"\')\";\r\n \r\n$sqlDateB4Query= $myQuery->query($sqlDateB4,\'SELECT\',\'NAME\');\r\n$tarikhTawaran =$sqlDateB4Query[0][\"TARIKHTAWARAN\"];\r\n$tarikhPeringatan = $sqlDateB4Query[0][\"TARIKHPERINGATAN\"];\r\n$tarikhAkhirPenjadualanSemula  = strftime(\"%d / %m / %Y\", strtotime($sqlDateB4Query[0][\"TARIKHAKHIRPENJADUALAN\"]));\r\n\r\n//email permohonan\r\n$txtMsg=\'<html>\r\n<head>\r\n<style type=\"text/css\">\r\n<!--\r\n.style30 {font-size: 14px;}\r\n.style53 {font-family: Arial, Helvetica, sans-serif; font-size: 16;}\r\n.style54 {font-family: Arial, Helvetica, sans-serif; font-size: 16; font-weight: bold; }\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<table width=\"847\" border=\"0\" cellpadding=\"0\" cellspacing=\"3\" class=\"style30\">\r\n  <tr class=\"style30\">\r\n    <td><span class=\"style53\">Tuan / Puan,\r\n      </span>\r\n      <p align=\"justify\" class=\"style53\"><br>\r\n      <br>\r\n      Adalah dimaklumkan bahawa permohonan tuan/puan untuk menduduki ujian Penilaian ICT Skills Assessment and Certification (ISAC) telah diterima. Maklumat permohonan adalah seperti berikut :-<br><br></p>    </td>\r\n  </tr>\r\n  <tr class=\"style30\">\r\n    <td>    </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"><table width=\"532\" border=\"0\" align=\"center\">\r\n      <tr>\r\n        <td width=\"252\" class=\"style54\">Nama Pemohon</td>\r\n        <td width=\"10\" class=\"style54\">:</td>\r\n        <td width=\"260\" class=\"style54\">\';\r\n		$txtMsg.= $nama_peserta;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"style54\">No MyKad / Polis / Pasport</td>\r\n        <td class=\"style54\">:</td>\r\n  