INSERT INTO `flc_bl` VALUES ('14', 'DAFTAR_KUMPULAN', 'Daftar_Kumpulan', '', 'include(\'db.php\');\r\n/*--------------------------------------------------------------------\r\n//pro_penyelaras\r\n$id_penyelaras = $_POST[\'input_map_65_4841\'];\r\n$nama_penyelaras = $_POST[\'input_map_65_4812\'];\r\n$emel_penyelaras = $_POST[\'input_map_65_4814\'];\r\n$kod_kementerian = $_POST[\'input_map_65_4816\'];\r\n$no_telefon_pejabat = $_POST[\'input_map_65_4837\'];\r\n$no_fax = $_POST[\'input_map_65_4839\'];\r\n$refId=$_POST[\'input_map_3334_9174\'][0];\r\n//pro_tempat_tugas\r\n$kod_jabatan= $_POST[\'input_map_65_4818\'];\r\n$bahagian = $_POST[\'input_map_65_4820\'];\r\n$alamat_1 = $_POST[\'input_map_65_4822\'];\r\n$alamat_2 = $_POST[\'input_map_65_4824\'];\r\n$alamat_3 = $_POST[\'input_map_65_4826\'];\r\n$poskod = $_POST[\'input_map_65_4828\'];\r\n$bandar = $_POST[\'input_map_65_4831\'];\r\n$kod_negeri = $_POST[\'input_map_65_4833\'];\r\n$kod_negara = $_POST[\'input_map_65_4835\'];\r\n\r\n//prs_permohonan_peserta\r\n$id_permohonan = $_POST[\'input_map_65_4919\'];\r\n//$kod_status= $_POST[\'input_map_3284_4919\'];\r\n\r\n$sql = \"update usr_isac.pro_penyelaras set\r\nnama_penyelaras=\'$nama_penyelaras\',emel_penyelaras=\'$emel_penyelaras\',kod_kementerian=\'$kod_kementerian\',no_telefon_pejabat=\'$no_telefon_pejabat\',no_fax=\'$no_fax\' where log_id_pengguna=\'{SESSION|publicID}\'\";\r\n\r\n\r\n$sql2 = \"insert into usr_isac.pro_tempat_tugas\r\n(kod_jabatan,bahagian,alamat_1,alamat_2,alamat_3,poskod,bandar,kod_negeri,kod_negara) \r\nvalues \r\n(\'$kod_jabatan\',\'$bahagian\',\'$alamat_1\',\'$alamat_2\',\'$alamat_3\',\'$poskod\',\'$bandar\',\'$kod_negeri\',\'$kod_negara\')\";\r\n\r\n//pro_peserta\r\n$id_peserta = $_POST[\'input_map_65_4918\'];\r\n$nama_peserta = $_POST[\'input_map_65_452\'];\r\n$no_kad_pengenalan = $_POST[\'input_map_65_480\'];\r\n$no_kad_pengenalan_lain = $_POST[\'input_map_65_4917\'];\r\n//pro_perkhidmatan\r\n$kod_klasifikasi_perkhidmatan = $_POST[\'input_map_65_451\'];\r\n$kod_gred_jawatan = $_POST[\'input_map_65_450\'];\r\n$kod_kategori_peserta = $_POST[\'input_map_65_5160\'];\r\n\r\nfor($x = 0; $x < count($nama_peserta); $x++)\r\n{\r\nif($nama_peserta[$x])\r\n{\r\n$sql3 = \"insert into usr_isac.pro_peserta\r\n(nama_peserta,no_kad_pengenalan,no_kad_pengenalan_lain,id_penyelaras,kod_kategori_peserta)\r\nvalues \r\n(\'$nama_peserta[$x]\',\'$no_kad_pengenalan[$x]\',\'$no_kad_pengenalan_lain[$x]\',(select max(id_penyelaras) from usr_isac.pro_penyelaras),\'$kod_kategori_peserta\')\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n$sql4 = \"insert into usr_isac.pro_perkhidmatan\r\n(kod_klasifikasi_perkhidmatan,kod_gred_jawatan,id_peserta) \r\nvalues \r\n(\'$kod_klasifikasi_perkhidmatan[$x]\',\'$kod_gred_jawatan[$x]\',(select max(id_peserta) from usr_isac.pro_peserta))\";\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n}\r\n}\r\n--------------------------------------------------------------------*/\r\n//prs permohonan\r\n$tahap = $_POST[\'input_map_3334_5210\'];\r\n$tujuan = $_POST[\'input_map_3334_5213\'];\r\n$pilihan_sesi = $_POST[\'input_map_3334_5212\'];\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n$id_permohonan=$_POST[\'input_map_3334_5215\'];\r\n\r\n/*\r\n$sql = \"insert into usr_isac.prs_permohonan\r\n(id_penyelaras,tarikh_permohonan,kod_tahap,kod_tujuan,id_sesi)\r\nvalues \r\n((select MAX(id_penyelaras) from usr_isac.pro_penyelaras),\'$tarikh\',\'$tahap\',\'$tujuan\',\'$pilihan_sesi\')\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n\r\n$sql2 = \"insert into usr_isac.prs_permohonan_peserta\r\n(id_peserta,id_permohonan)\r\nvalues \r\n(\'$id_peserta\',\'$id_permohonan\')\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n*/\r\n\r\n$sqlSesi=\"SELECT DISTINCT\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'TARIKH_NILAI\',\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'MASA NILAI\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'LOKASI_NILAI\',\r\na.lokasi AS \'Lokasi Penilaian\',\r\na.ID_SESI as \'ID_SESI\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'TAHAP\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_tahap) AS \'TAHAP\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'TUJUAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_tujuan) AS \'TUJUAN\'\r\n\r\nFROM usr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_penyelaras c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_penyelaras=c.id_penyelaras) \r\nAND c.log_id_pengguna=\'{SESSION|publicID}\'\r\nORDER BY a.id_sesi\";\r\n$sqlSesiRs = $myQuery->query($sqlSesi,\'SELECT\',\'NAME\');\r\n$countRowSesi=count($sqlSesiRs);\r\n\r\n$tahap= $sqlSesiRs[0][\"TAHAP\"];\r\n$tujuan= $sqlSesiRs[0][\"TUJUAN\"];\r\n$refId=$_POST[\'input_map_3334_9174\'][0];\r\n\r\n\r\n\r\n$sqlTempoh = \"select emel_tawaran_penilaian_kumpulan as \'TEMPOHTAWARAN\',\r\n				 emel_peringatan_sebelum_tarikh_penilaian as \'TEMPOHPERINGATAN\',\r\n				 tempoh_tutup_tarikh_penilaian_kumpulan as \'TEMPOHTUTUP\'\r\n				 from usr_isac.pro_kawalan_sistem\";\r\n				 \r\n$sqlTempohRs= $myQuery->query($sqlTempoh,\'SELECT\',\'NAME\');\r\n$tempohTawaran = $sqlTempohRs[0][\"TEMPOHTAWARAN\"];\r\n$tempohPeringatan = $sqlTempohRs[0][\"TEMPOHPERINGATAN\"];\r\n$tempohTutup = $sqlTempohRs[0][\"TEMPOHTUTUP\"];\r\n				 \r\nfor($x=0; $x<$countRowSesi; $x++)\r\n{\r\n			\r\n			$sesiku = $sqlSesiRs[$x][\"ID_SESI\"];\r\n			$sqlSenaraiPeserta=\"select \r\n			d.ID_SESI, \r\n			date_add(date_format(d.tarikh_sesi,\'%Y-%m-%D\'), interval -\'\".$tempohTawaran.\"\' day) AS \'TARIKH_TAWARAN\',\r\n                       date_add(date_format(d.tarikh_sesi,\'%Y-%m-%D\'), interval -\'\".$tempohPeringatan.\"\' day) AS \'TARIKH_PERINGATAN\',\r\n						date_add(date_format(d.tarikh_sesi,\'%Y-%m-%d\'), interval -\'\".$tempohTutup.\"\' day) AS \'TARIKHAKHIRPENJADUALAN\',\r\n			date_format(d.tarikh_sesi,\'%d-%m-%Y\') AS \'TARIKH\',\r\n			concat(\r\n			(SELECT description1 FROM refgeneral \r\n			WHERE\r\n			mastercode=(SELECT referencecode\r\n			FROM refgeneral WHERE mastercode=\'XXX\' \r\n			AND description1=\'masa_mula\') \r\n			AND\r\n			referencestatuscode=\'00\' \r\n			AND referencecode=d.kod_masa_mula),\r\n			\'-\',\r\n			(SELECT description1 FROM refgeneral \r\n			WHERE\r\n			mastercode=(SELECT referencecode\r\n			FROM refgeneral WHERE mastercode=\'XXX\' \r\n			AND description1=\'masa_tamat\') \r\n			AND\r\n			referencestatuscode=\'00\' \r\n			AND referencecode=d.kod_masa_tamat)) AS \'MASA\',\r\n			\r\n			if(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain) as \'NO_KP\',\r\n			a.nama_peserta as \'NAMA\',\r\n			d.LOKASI as \'LOKASI\',\r\n			c.EMEL_PENYELARAS as \'EMEL_PENYELARAS\'\r\n			from\r\n			usr_isac.pro_peserta a,\r\n			usr_isac.pro_perkhidmatan b,\r\n			usr_isac.pro_penyelaras c,\r\n			usr_isac.pro_sesi d,\r\n			usr_isac.prs_permohonan e\r\n			where\r\n			a.id_peserta=b.id_peserta\r\n			and\r\n			a.id_penyelaras=c.id_penyelaras\r\n			and\r\n			a.id_peserta=e.id_peserta\r\n			and\r\n			e.id_sesi=d.id_sesi\r\n			and\r\n			c.id_penyelaras=e.id_penyelaras\r\n			and\r\n			d.id_sesi=\'$sesiku\' and\r\n			c.log_id_pengguna=\'{SESSION|publicID}\' \r\n			ORDER BY d.ID_SESI, a.NAMA_PESERTA\";\r\n			$sqlSenaraiPesertaRs = $myQuery->query($sqlSenaraiPeserta,\'SELECT\',\'NAME\');\r\n			$countRowSenaraiPeserta=count($sqlSenaraiPesertaRs);\r\n			$emelPenyelaras = $sqlSenaraiPesertaRs[$x][\"EMEL_PENYELARAS\"];\r\n			$tarikhTawaran = $sqlSenaraiPesertaRs[$x][\"TARIKH_TAWARAN\"];\r\n            $tarikhPeringatan = $sqlSenaraiPesertaRs[$x][\"TARIKH_PERINGATAN\"];\r\n			$tarikhAkhirPenjadualan = strftime(\"%d / %m / %Y\", strtotime($sqlSenaraiPesertaRs[$x][\"TARIKHAKHIRPENJADUALAN\"]));\r\n	$txtMsg=\'\r\n	<html>\r\n	<head>\r\n	<style type=\"text/css\">\r\n	<!--\r\n	.style30 {font-size: 14px;}\r\n	.style53 {font-family: Arial, Helvetica, sans-serif; font-size: 16;}\r\n	.style54 {font-family: Arial, Helvetica, sans-serif; font-size: 16; font-weight: bold; }\r\n	-->\r\n	</style>\r\n	</head>\r\n	\r\n	<body>\r\n	<table width=\"847\" border=\"0\" cellpadding=\"0\" cellspacing=\"3\" class=\"style30\">\r\n	  <tr class=\"style30\">\r\n		<td><span class=\"style53\">Tuan / Puan,\r\n		  </span>\r\n		  <p align=\"justify\" class=\"style53\"><br />\r\n				<br />\r\n		</p></td>\r\n	  </tr>\r\n	  <tr class=\"style30\">\r\n		<td class=\"style53\"><p>Adalah  dimaklumkan bahawa permohonan peserta dari jabatan/ajensi tuan/puan untuk  menduduki ujian Penilaian IT Skill Assessment and Certification (ISAC) telah  diterima. Maklumat permohonan adalah seperti berikut :-</p></td>\r\n	  </tr>\r\n	  <tr class=\"style30\">\r\n		<td> </td>\r\n	  </tr>\r\n	  <tr>\r\n		<td height=\"16\" class=\"style53\"><table width=\"532\" border=\"0\" align=\"center\">\r\n		  <tr>\r\n			<td width=\"252\" class=\"style54\">Nama Penilaian</td>\r\n			<td width=\"10\" class=\"style54\">:</td>\r\n			<td width=\"260\" class=\"style54\">Penilaian ISAC bagi Agensi</td>\r\n		  </tr>\r\n		  <tr>\r\n			<td class=\"style54\">Tahap Peni