permohonan set\r\ntarikh_kemaskini_permohonan=\'$tarikh\',kod_tujuan=\'$tujuan\' where id_peserta=\'$id_peserta\'\";\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\n//panjadualan semula id_sesi=\'$\'\r\n//$result4 = $myQuery->query($sql4,\'RUN\');\r\n//}\r\n//$sql5 = \"update usr_isac.prs_permohonan_peserta set\r\n//id_peserta=\'$id_peserta\',id_permohonan=\'$id_permohonan\' where id_permohonan=\'$id_peserta\'\";\r\n//$result5 = $myQuery->query($sql5,\'RUN\');\r\n\r\nif($result && $result2 && $result3 && $result4)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n\r\n}', '', '', '00', '1', '2009-10-23', '1', '2010-03-17');
INSERT INTO `flc_bl` VALUES ('22', 'PENJADUALAN_SEMULA_INDIVIDU', 'PENJADUALAN_SEMULA_INDIVIDU', '', 'include(\'db.php\');\r\n\r\n//pro_peserta\r\n$id_peserta = $_POST[\'input_map_3312_5099\'];\r\n//prs_permohonan\r\n\r\n$id_sesi_asal = $_POST[\'input_map_3312_5208\'];\r\n\r\n\r\n//syarat tiada kekosongan tempat\r\n$pilihan_sesi = $_POST[\'input_map_3313_5047\'];\r\n$tahap_penilaian=$_POST[\'input_map_3312_5030\'];\r\n\r\n//carian jadual penilaian\r\n$bulan = $_POST[\'input_map_3312_11356\'];\r\n$tahun = $_POST[\'input_map_3312_5031\'];\r\n\r\n$j = $_POST[\'input_map_3313_5047\'];\r\nfor($x = 0; $x < count($j); $x++)\r\n{\r\n\r\n//check bilangan kekosongan.\r\n$check=\"select bilangan_peserta from usr_isac.pro_sesi where id_sesi=\'$pilihan_sesi[$x]\' \";\r\n$checkRs=$myQuery->query($check,\'SELECT\');\r\n$empty = $checkRs[0][0];\r\n\r\nif ($empty == 0) \r\n{\r\n 	echo \"<script language=javascript>alert(\'Maaf, tiada kekosongan pada sesi ini,sila pilih sesi penilaian lain.\'); </script>\";\r\n	redirect(\'index.php?page=page_wrapper&menuID=3208&sesi=\'.$id_sesi_asal.\'&tahap_penilaian=\'.$tahap_penilaian);\r\n}  \r\n\r\nelse\r\n{\r\n\r\n$j = $_POST[\'input_map_3313_5047\'];\r\nfor($x = 0; $x < count($j); $x++)\r\n{\r\n $sql = \"update usr_isac.prs_permohonan a,usr_isac.pro_peserta b set \r\n	a.id_sesi=\'$pilihan_sesi[$x]\',sebab_batal=\'\',tarikh_batal_permohonan=\'\' where a.id_peserta=b.id_peserta and (b.no_kad_pengenalan=\'{SESSION|publicID}\' or b.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\";\r\n//echo $sql;\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta-1) where id_sesi=\'$pilihan_sesi[$x]\'\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n}\r\n\r\nif($result2)\r\n{\r\n$sql3 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta+1) where id_sesi=\'$id_sesi_asal\'\";\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n}\r\n\r\nif($result)\r\n{\r\n\r\n$sesi=\"select b.id_sesi,b.kod_tahap from usr_isac.pro_peserta a, usr_isac.prs_permohonan b,usr_isac.pro_sesi c where\r\na.id_peserta = b.id_peserta and b.id_sesi = c.id_sesi and (a.no_kad_pengenalan=\'{SESSION|publicID}\' or a.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\";\r\n$sesiRs=$myQuery->query($sesi,\'SELECT\');\r\n\r\n       $sesi = $sesiRs[0][0];\r\n       $kod_tahap = $sesiRs[0][1];\r\n\r\n\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=35&sesi=\'.$sesi.\'&tahap_penilaian=\'.$kod_tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}\r\n\r\n//--------For flc_notification_item -------\r\n \r\n$sqlMaklumatSesi=\"SELECT \r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'SESI\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'TARIKH\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'MASA\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'PUSAT\',\r\nc.NAMA_PESERTA AS \'NAMA_PESERTA\',\r\nc.EMEL_PESERTA AS \'EMEL_PESERTA\',\r\nd.EMEL_PENYELIA AS \