kan nama tuan/puan <strong>DISENARAIHITAMKAN</strong> daripada menduduki ujian penilaian di masa akan  datang.</p>    </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>Sebarang kemusykilan/masalah, sila hubungi kami melalui emel:  <a href=\"mailto:isachelp@intanbk.intan.my\">isachelp@intanbk.intan.my</a>.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>Sekian, terima  kasih.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>Pentadbir  ISAC<br />\r\n      Institut  Tadbiran Awam Negara<br />\r\n    Jabatan  Perkhidmatan Awam, Malaysia</p></td>\r\n  </tr>\r\n</table>\r\n</body>\r\n</html>\r\n\';\r\n\r\nif($sqlCekHantarEmelRs[0][0] == 0)\r\n{\r\n//immediately\r\nnotification(\'NOTIFIKASI_EMAIL_PERMOHONAN_PERNILAIAN_PESERTA\',\"EMAIL_TO=\'\".$emel_peserta.\"\'\",\"EMAIL_CC=\'\".$emel_penyelia.\"\'\",\"EMAIL_FROM=\'isacadmin@intanbk.intan.my\'\",\"EMAIL_SUBJECT=\'Permohonan Penilaian ICT Skills Assessment and Certification (ISAC) \'\",\"EMAIL_MESSAGE=\'\".$txtMsg.\"\'\",\"REFFERENCE_ID=\'{SESSION|publicID}\'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EXECUTION_DATE=curdate()\",\"PROGRAM_TYPE=\'EMEL_PERMOHONAN\'\");\r\n\r\n//tawaran\r\nnotification(\'NOTIFIKASI_EMAIL_TAWARAN_PERNILAIAN_PESERTA\',\"EMAIL_TO=\'\".$emel_peserta.\"\'\",\"EMAIL_CC=\'\".$emel_penyelia.\"\'\",\"EMAIL_FROM=\'isacadmin@intanbk.intan.my\'\",\"EMAIL_SUBJECT=\'Tawaran Penilaian ICT Skills Assessment and Certification (ISAC) \'\",\"EMAIL_MESSAGE=\'\".$txtMsg2.\"\'\",\"REFFERENCE_ID=\'{SESSION|publicID}\'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EXECUTION_DATE=\'\".$tarikhTawaran.\"\'\",\"PROGRAM_TYPE=\'EMEL_TAWARAN\'\");\r\n\r\n//peringatan\r\nnotification(\'NOTIFIKASI_EMAIL_PERINGATAN_PERNILAIAN_PESERTA\',\"EMAIL_TO=\'\".$emel_peserta.\"\'\",\"EMAIL_CC=\'\".$emel_penyelia.\"\'\",\"EMAIL_FROM=\'isacadmin@intanbk.intan.my\'\",\"EMAIL_SUBJECT=\'Peringatan : Penilaian ICT Skills Assessment and Certification (ISAC) \'\",\"EMAIL_MESSAGE=\'\".$txtMsg3.\"\'\",\"REFFERENCE_ID=\'{SESSION|publicID}\'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EXECUTION_DATE=\'\".$tarikhPeringatan.\"\'\",\"PROGRAM_TYPE=\'EMEL_PERINGATAN\'\");\r\n}\r\nelse\r\n{\r\n//update tarikh permohonan\r\n$sqlUpdateTarikhPermohonan= \"update flc_notification_item set \r\nEXECUTION_DATE = curdate(),\r\nNOTIFICATION_ITEM_STATUS = \'Open\',\r\nEMAIL_MESSAGE=\'\".$txtMsg.\"\'\r\nwhere\r\nREFFERENCE_ID = \'{SESSION|publicID}\' AND\r\nPROGRAM_TYPE = \'EMEL_PERMOHONAN\'\";\r\n$sqlUpdateTarikhPermohonan = $myQuery->query($sqlUpdateTarikhPermohonan,\'RUN\');\r\n\r\n//update tarikh tawaran\r\n$sqlUpdateTarikhTawaran= \"update flc_notification_item set \r\nEXECUTION_DATE = \'\".$tarikhTawaran.\"\',\r\nNOTIFICATION_ITEM_STATUS = \'Open\',\r\nEMAIL_MESSAGE=\'\".$txtMsg2.\"\'\r\nwhere\r\nREFFERENCE_ID = \'{SESSION|publicID}\' AND\r\nPROGRAM_TYPE = \'EMEL_TAWARAN\'\";\r\n$runUpdateTawaran = $myQuery->query($sqlUpdateTarikhTawaran,\'RUN\');\r\n\r\n//update tarikh peringatan\r\n$sqlUpdateTarikhPeringatan= \"update flc_notification_item set \r\nEXECUTION_DATE = \'\".$tarikhPeringatan.\"\',\r\nNOTIFICATION_ITEM_STATUS = \'Open\',\r\nEMAIL_MESSAGE=\'\".$txtMsg3.\"\'\r\nwhere\r\nREFFERENCE_ID = \'{SESSION|publicID}\' AND\r\nPROGRAM_TYPE = \'EMEL_PERINGATAN\'\";\r\n$sqlUpdateTarikhPeringatan = $myQuery->query($sqlUpdateTarikhPeringatan,\'RUN\');\r\n}\r\n}//eof syarat kekosongan tempat\r\n}//eof for loop ', '', '', '00', '1', '2009-10-24', '1', '2010-03-25');
INSERT INTO `flc_bl` VALUES ('23', 'SEMAK_STATUS_PESERTA_INDIVIDU', 'SEMAK_STATUS_PESERTA_INDIVIDU', '', 'include(\'db.php\');\r\n\r\n$tahap_penilaian=$_POST[\'input_map_238_1349\'];\r\n$tujuan_permohonan=$_POST[\'input_map_238_1348\'];\r\n\r\n//if ((document.getElementsId(\'input_map_238_4591[]\') != \'{SESSION|public_ID}\')\r\nif (\'{POST|input_map_238_4591}\' != \'{SESSION|publicID}\')\r\n{\r\necho \"<script language=javascript>alert(\'No.MyKad/Polis/Tentera/Passport yang anda masukkan tidak sama, sila masukkan semula\'); </script>\";\r\n		 redirect(\'index.php?logout=true\');\r\n}\r\n\r\nelse\r\n{\r\n\r\n$sesi=\"select b.id_sesi from usr_isac.pro_peserta a, usr_isac.prs_permohonan b,usr_isac.pro_sesi c where\r\na.id_peserta = b.id_peserta and b.id_sesi = c.id_sesi and (a.no_kad_pengenalan=\'{SESSION|publicID}\' or a.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\";\r\n$sesiRs=$myQuery->query($sesi,\'SELECT\');\r\n\r\n       $sesi = $sesiRs[0][0];\r\n\r\n//search active -->peserta telah daftar dlm kategori kumpulan dan belum menduduki penilaian (either kt cni @ ms login??)\r\n$daftar=\"select a.id_peserta\r\n			from usr_isac.prs_permohonan a, usr_isac.pro_peserta b\r\n			where \r\n			b.kod_kategori_peserta=\'02\' and a.kod_status_permohonan=\'01\' \r\n                        and (b.no_kad_pengenalan=\'{SESSION|publicID}\' or b.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\r\n			and a.id_peserta=b.id_peserta\";\r\n$daftarRs=$myQuery->query($daftar,\'SELECT\');\r\n\r\n//search lulus -->peserta telah lulus penilaian basic\r\n$lulusBasic=\"select a.id_peserta\r\n	    from usr_isac. prs_penilaian_peserta a, usr_isac.pro_peserta b\r\n	     where b.kod_kategori_peserta=\'01\' \r\n	     and a.kod_status_kelulusan=\'Lulus\'\r\n                          and (b.no_kad_pengenalan=\'{SESSION|publicID}\' or b.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\r\n	     and a.id_peserta=b.id_peserta\";\r\n$lulusBasicRs=$myQuery->query($lulusBasic,\'SELECT\');\r\n\r\n//search gagal -->peserta gagal, utk mohon semula perlu melepasi tempoh sebulan(kawalan sistem) dari tarikh penilaian\r\n$gagal=\"select c.id_peserta\r\n			from usr_isac.pro_sesi a,usr_isac.pro_kawalan_sistem b,usr_isac.pro_peserta c,usr_isac.prs_permohonan d,usr_isac.prs_penilaian_peserta e\r\n			where \r\n                        (c.no_kad_pengenalan=\'{SESSION|publicID}\' or c.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\r\n			and a.id_sesi=d.id_sesi and d.id_peserta=c.id_peserta and c.id_peserta=e.id_peserta\r\n			and b.id_kawalan_sistem=\'1\' and e.kod_status_kelulusan=\'Gagal\'\r\n			and  adddate(a.tarikh_sesi,b.tempoh_kebenaran_permohonan_peserta_gagal) >=curdate()\";\r\n$gagalRs=$myQuery->query($gagal,\'SELECT\');\r\n\r\n//search blacklist -->peserta yg tidak hadir penilaian dan tidak membuat rayuan kepada admin, utk mohon msemula perlu melepasi tempoh 6 bln(kawalan sistem)\r\n$blacklist=\"select c.id_peserta\r\n				from usr_isac.pro_sesi a,usr_isac.pro_kawalan_sistem b,usr_isac.pro_peserta c,usr_isac.prs_permohonan d,usr_isac.prs_penilaian_peserta e\r\n				where \r\n                                (c.no_kad_pengenalan=\'{SESSION|publicID}\' or c.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\r\n				and a.id_sesi=d.id_sesi and d.id_peserta=c.id_peserta and c.id_peserta=e.id_peserta\r\n				and e.kod_status_kehadiran=\'02\'\r\n				and adddate(a.tarikh_sesi,b.tempoh_kebenaran_permohonan_peserta_blacklist) >= curdate()\r\n				\";\r\n$blacklistRs=$myQuery->query($blacklist,\'SELECT\');\r\n\r\n//penah daftar\r\n$all = \"select no_kad_pengenalan,no_kad_pengenalan_lain from usr_isac.pro_peserta where (no_kad_pengenalan=\'{SESSION|publicID}\' or no_kad_pengenalan_lain=\'{SESSION|publicID}\') \";\r\n\r\n$allRs = $myQuery->query($all,\'SELECT\');\r\n\r\n//if basic\r\nif($tahap_penilaian==\'01\')\r\n{\r\n	//if active, keluar\r\n	if($daftarRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, nama anda telah dicalonkan  dalam jadual bagi peserta  kategori kumpulan.\'); </script>\";\r\n		$location=1;	\r\n	}\r\n	else if ($lulusBasicRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Anda telah lulus penilaian tahap asas, untuk meneruskan permohonan, sila pilih tahap penilaian lanjutan\'); </script>\";\r\n		$location=1;\r\n	}	\r\n	else if ($gagalRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, anda tidak dibenarkan memohon semula dalam tempoh kurang daripada satu bulan daripada tarikh menduduki penilaian yang terakhir.\'); </script>\";\r\n		$location=1;\r\n	}\r\n	else if ($blacklistRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, nama anda telah disenaraihitamkan. Sila hantar rayuan melalui emel kepada isachelp@intanbk.intan.my atau anda hanya boleh memohon semula selepas 6 bulan dari tarikh penilaian yang terakhir\'); </script>\";\r\n		$location=1;\r\n	}		\r\n	//if not active, masuk\r\n               	else if(count($allRs) < 1) \r\n	{\r\n		$location=2;\r\n	}\r\n         else\r\n         {\r\n              $location=3;\r\n         }\r\n\r\n}\r\n//advance\r\nelse \r\n{\r\n        //not active, baru nak daftar permohonan\r\n        if(count($allRs) < 1) \r\n	{ \r\n           echo \"<script language=javascript>alert(\'Maaf, anda perlu memohon penilaian Tahap Asas terlebih dahulu.\'); </script>\";\r\n		$location=1;\r\n            	}\r\n           else if(!$lulusBasicRs) \r\n	{ \r\n                \r\n              echo \"<script language=javascript>alert(\'Maaf, anda belum melepasi penilaian Tahap Asas.\'); </script>\";\r\n		$location=1;\r\n            	}\r\n}\r\n\r\n//switch location\r\nswitch($location)\r\n{\r\n	//keluar sistem\r\n	case 1: redirect(\'index.php?page=page_wrapper&menuID=171&id=\'.$_POST[\'input_map_238_4591\']);\r\n		break;\r\n	//permohonan baru\r\n	case 2: redirect(\'index.php?page=page_wrapper&menuID=24&tahap_penilaian=\'.$_POST[\'input_map_238_1349\']);\r\n		break;\r\n	//kemaskini/penjadualan semula	\r\n	//case 3: redirect(\'index.php?page=page_wrapper&menuID=25\');\r\n                     case 3: redirect(\'index.php?page=page_wrapper&menuID=25&tahap_penilaian=\'.$_POST[\'input_map_238_1349\'].\'&sesi=\'.$sesi);\r\n 		break;\r\n}\r\n}\r\n', '', '', '00', '1', '2009-10-25', '1', '2010-02-27');
INSERT INTO `flc_bl` VALUES ('24', 'KEMASKINI_PERMOHONAN_KUMPULAN', 'KEMASKINI_PERMOHONAN_KUMPULAN', '', 'include(\'db.php\');\r\n//pro_tempat_tugas\r\n$id_tempat_tugas = $_POST[\'input_map_53_5206\'];\r\n$id_penyelaras = $_POST[\'input_map_53_5202\'];\r\n$nama_penyelaras= $_POST[\'input_map_53_393\'];\r\n$emel_penyelaras = $_POST[\'input_map_53_394\'];\r\n$kod_kementerian = $_POST[\'input_map_53_395\'];\r\n$kod_jabatan = $_POST[\'input_map_53_396\'];\r\n$bahagian = $_POST[\'input_map_53_397\'];\r\n$alamat_1 = $_POST[\'input_map_53_398\'];\r\n$alamat_2 = $_POST[\'input_map_53_847\'];\r\n$alamat_3 = $_POST[\'input_map_53_848\'];\r\n$poskod = $_POST[\'input_map_53_399\'];\r\n$bandar = $_POST[\'input_map_53_392\'];\r\n$kod_negeri = $_POST[\'input_map_53_400\'];\r\n$kod_negara = $_POST[\'input_map_53_401\'];\r\n$no_telefon_pejabat = $_POST[\'input_map_53_402\'];\r\n$no_fax = $_POST[\'input_map_53_403\'];\r\n\r\n//prs permohonan\r\n$kod_tahap = $_POST[\'input_map_111_5107\'];\r\n$kod_tujuan = $_POST[\'input_map_111_404\'];\r\n$id_permohonan = $_POST[\'input_map_111_5207\'];\r\n\r\n$sql = \"update usr_isac.pro_penyelaras set\r\nnama_penyelaras=\'$nama_penyelaras\',emel_penyelaras=\'$emel_penyelaras\',kod_kementerian=\'$kod_kementerian\',no_telefon_pejabat=\'$no_telefon_pejabat\',no_fax=\'$no_fax\' where log_id_pengguna=\'{SESSION|publicID}\'\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_tempat_tugas set\r\nkod_jabatan=\'$kod_jabatan\',bahagian=\'$bahagian\',alamat_1=\'$alamat_1\',alamat_2=\'$alamat_2\',alamat_3=\'$alamat_3\',poskod=\'$poskod\',bandar=\'$bandar\',kod_negeri=\'$kod_negeri\',kod_negara=\'$kod_negara\' where id_tempat_tugas=\'$id_tempat_tugas\'\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\nif($result && $result2)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=70\');\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n\r\n}\r\n', '', '', '00', '1', '2009-10-27', '1', '2009-11-07');
INSERT INTO `flc_bl` VALUES ('25', 'PEMBATALAN_JADUAL_PENILAIAN', 'PEMBATALAN_JADUAL_PENILAIAN', '', 'include(\'db.php\');\r\n\r\n$id_peserta = $_POST[\'input_map_119_5161\'];\r\n$sebab_pembatalan = $_POST[\'input_map_3317_887\'];\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n$kod_tahap = $_POST[\'input_map_3317_9273\'];\r\n$id_sesi_asal = $_POST[\'input_map_3317_5209\'];\r\n\r\n//$sql = \"update usr_isac.prs_permohonan set id_sesi=\'\',sebab_batal=\'$sebab_pembatalan\' where id_peserta=\'$id_peserta\'\";\r\n\r\n$sql = \"update usr_isac.prs_permohonan a,usr_isac.pro_peserta b set \r\na.id_sesi=\'\',a.sebab_batal=\'$sebab_pembatalan\',tarikh_batal_permohonan=\'$tarikh\' where a.id_peserta=b.id_peserta and (b.no_kad_pengenalan=\'{SESSION|publicID}\' or b.no_kad_pengenalan_lain=\'{SESSION|publicID}\')\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta+1) where id_sesi=\'$id_sesi_asal\'\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\nif($result)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Pembatalan Jadual Berjaya</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=35&tahap_penilaian=\'.$kod_tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Pembatalan Jadual Gagal</div>\";\r\n\r\n}', '', '', '00', '1', '2009-10-27', '1', '2010-01-27');
INSERT INTO `flc_bl` VALUES ('26', 'KEMASKINI_MAKLUMAT_PENYELARAS', 'KEMASKINI_MAKLUMAT_PENYELARAS', '', 'include(\'db.php\');\r\n\r\n//pro_penyelaras\r\n$id_penyelaras = $_POST[\'input_map_5_5169\'];\r\n$nama_penyelaras = $_POST[\'input_map_5_32\'];\r\n$emel_penyelaras = $_POST[\'input_map_5_33\'];\r\n$kod_kementerian = $_POST[\'input_map_5_34\'];\r\n$no_telefon_pejabat = $_POST[\'input_map_5_43\'];\r\n$no_fax = $_POST[\'input_map_5_44\'];\r\n\r\n//pro_tempat_tugas\r\n$kod_jabatan= $_POST[\'input_map_5_36\'];\r\n$bahagian = $_POST[\'input_map_5_35\'];\r\n$alamat_1 = $_POST[\'input_map_5_37\'];\r\n$alamat_2 = $_POST[\'input_map_5_843\'];\r\n$alamat_3 = $_POST[\'input_map_5_844\'];\r\n$poskod = $_POST[\'input_map_5_38\'];\r\n$bandar = $_POST[\'input_map_5_40\'];\r\n$kod_negeri = $_POST[\'input_map_5_41\'];\r\n$kod_negara = $_POST[\'input_map_5_42\'];\r\n\r\n$sql = \"update usr_isac.pro_penyelaras set\r\nnama_penyelaras=\'$nama_penyelaras\',emel_penyelaras=\'$emel_penyelaras\',kod_kementerian=\'$kod_kementerian\',no_telefon_pejabat=\'$no_telefon_pejabat\',no_fax=\'$no_fax\' where log_id_pengguna=\'{SESSION|publicID}\'\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_tempat_tugas set\r\nkod_kementerian=\'$kod_kementerian\',kod_jabatan=\'$kod_jabatan\',bahagian=\'$bahagian\',alamat_1=\'$alamat_1\',alamat_2=\'$alamat_2\',alamat_3=\'$alamat_3\',poskod=\'$poskod\',bandar=\'$bandar\',kod_negeri=\'$kod_negeri\',kod_negara=\'$kod_negara\',nama_penyelia=\'$nama_penyelaras\',emel_penyelia=\'$emel_penyelaras\',no_telefon_penyelia=\'$no_telefon_pejabat\',no_fax_penyelia=\'$no_fax\' where id_penyelaras=\'$id_penyelaras\'\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\nif($result && $result2)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n\r\n}', '', '', '00', '1', '2009-10-2