INSERT INTO `flc_bl` VALUES ('28', 'DAFTAR_PESERTA_KUMPULAN', 'DAFTAR_PESERTA_KUMPULAN', '', 'include(\'db.php\');\r\n\r\n//pro_peserta\r\n$id_peserta = $_POST[\'input_map_3330_5200\'];\r\n$id_penyelaras = $_POST[\'input_map_3330_5201\'];\r\n$nama_peserta = $_POST[\'input_map_3330_5166\'];\r\n$no_kad_pengenalan = $_POST[\'input_map_3330_5165\'];\r\n$no_kad_pengenalan_lain = $_POST[\'input_map_3330_5164\'];\r\n\r\n//$bil = $_POST[\'input_map_3330_11346\'];\r\n//pro_perkhidmatan\r\n$kod_klasifikasi_perkhidmatan = $_POST[\'input_map_3330_5167\'];\r\n$kod_gred_jawatan = $_POST[\'input_map_3330_5168\'];\r\n\r\n//prs_permohonan\r\n$pilihan_sesi = $_POST[\'input_map_3330_5239\'];\r\n$tahap = $_POST[\'input_map_3330_5238\'];\r\n$tujuan = $_POST[\'input_map_3330_5241\'];\r\n//$tujuan = \'04\';\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n$id_permohonan=$_POST[\'input_map_3334_5215\'];\r\n$kod_status_permohonan=$_POST[\'input_map_3330_8663\'];\r\n$hdn_ic=$_POST[\'input_map_3330_8664\'];\r\n\r\n//pro_penyelaras\r\n$nama_penyelaras = $_POST[\'input_map_3330_9118\'];\r\n$emel_penyelaras = $_POST[\'input_map_3330_9119\'];\r\n$kod_kementerian = $_POST[\'input_map_3330_9120\'];\r\n$no_telefon_pejabat = $_POST[\'input_map_3330_9121\'];\r\n$no_fax = $_POST[\'input_map_3330_9122\'];\r\n\r\n//pro_tempat_tugas\r\n$kod_jabatan= $_POST[\'input_map_3330_9123\'];\r\n$bahagian = $_POST[\'input_map_3330_9124\'];\r\n$alamat_1 = $_POST[\'input_map_3330_9125\'];\r\n$alamat_2 = $_POST[\'input_map_3330_9126\'];\r\n$alamat_3 = $_POST[\'input_map_3330_9127\'];\r\n$poskod = $_POST[\'input_map_3330_9128\'];\r\n$bandar = $_POST[\'input_map_3330_9129\'];\r\n$kod_negeri = $_POST[\'input_map_3330_9130\'];\r\n$kod_negara = $_POST[\'input_map_3330_9131\'];\r\n\r\n/*\r\n//if ((\'{POST|input_map_3331_5171}\' != \'$no_kad_pengenalan\'))\r\n        //if not match, keluar\r\n         if (\'{POST|input_map_3330_5165}\' != \'$hdn_ic\')\r\n        {\r\n              echo \"<script language=javascript>alert(\'No.MyKad/Polis/Tentera/Passport yang anda masukkan tidak sama, sila masukkan semula\'); </script>\";\r\n	     redirect(\'index.php?page=page_wrapper&menuID=3211&tahap_penilaian=\'.$tahap.\'&tujuan=\'.$tujuan.\'&sesi=\'.$pilihan_sesi);\r\n        }\r\nelse\r\n{\r\n*/\r\n//carian jadual penilaian\r\n$bulan = $_POST[\'input_map_3282_4636\'];\r\n$tahun = $_POST[\'input_map_3282_4637\'];\r\n/*\r\n$j = $_POST[\'input_map_3330_5239\'];\r\nfor($x = 0; $x < count($j); $x++)\r\n{\r\n*/\r\n\r\n//check bilangan kekosongan.\r\n$check=\"select bilangan_peserta from usr_isac.pro_sesi where id_sesi=\'$pilihan_sesi\' \";\r\n$checkRs=$myQuery->query($check,\'SELECT\');\r\n$empty = $checkRs[0][0];\r\n\r\n//tiada kekosongan\r\nif ($empty == 0) \r\n{\r\n 	echo \"<script language=javascript>alert(\'Maaf, tiada kekosongan pada sesi ini,sila pilih sesi penilaian lain.\'); </script>\";\r\n	redirect(\'index.php?page=page_wrapper&menuID=68\');\r\n}  \r\n\r\n//ada kekosongan\r\nelse\r\n{\r\n\r\n\r\n$sql = \"insert into usr_isac.pro_peserta\r\n(nama_peserta,no_kad_pengenalan,no_kad_pengenalan_lain,kod_kategori_peserta,id_penyelaras)\r\nvalues \r\n(\'$nama_peserta\',\'$no_kad_pengenalan\',\'$no_kad_pengenalan_lain\',\'02\',\'$id_penyelaras\')\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"insert into usr_isac.pro_perkhidmatan\r\n(kod_klasifikasi_perkhidmatan,kod_gred_jawatan,id_peserta)\r\nvalues \r\n(\'$kod_klasifikasi_perkhidmatan\',\'$kod_gred_jawatan\',(select max(id_peserta) from usr_isac.pro_peserta))\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n//penyelia\r\n$sql6 = \"insert into usr_isac.pro_tempat_tugas (id_peserta,kod_kementerian,kod_jabatan,bahagian,alamat_1,alamat_2,alamat_3,poskod,bandar,kod_negeri,kod_negara,nama_penyelia,emel_penyelia,no_telefon_penyelia,no_fax_penyelia)\r\nvalues\r\n((select max(id_peserta) from usr_isac.pro_peserta),\'$kod_kementerian\',\'$kod_jabatan\',\'$bahagian\',\'$alamat_1\',\'$alamat_2\',\'$alamat_3\',\'$poskod\',\'$bandar\',\'$kod_negeri\',\'$kod_negara\',\'$nama_penyelaras\',\'$emel_penyelaras\',\'$no_telefon_pejabat\',\'$no_fax\') \";\r\n\r\n$result6 = $myQuery->query($sql6,\'RUN\');\r\n\r\n$sql3 = \"insert into usr_isac.prs_permohonan\r\n(id_penyelaras,id_peserta,kod_status_permohonan,kod_status_kehadiran,tarikh_permohonan,kod_tahap,kod_tujuan,id_sesi)\r\nvalues \r\n(\'$id_penyelaras\',(select max(id_peserta) from usr_isac.pro_peserta),\'$kod_status_permohonan\',\'02\',\'$tarikh\',\'$tahap\',\'$tujuan\',\'$pilihan_sesi\')\";\r\n\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n$sql5 = \"insert into usr_isac.prs_permohonan_peserta\r\n(id_peserta,id_permohonan,kod_status)\r\nvalues \r\n((select max(id_peserta) from usr_isac.pro_peserta),(select max(id_permohonan) from usr_isac.prs_permohonan),\'00\')\";\r\n\r\n$result5 = $myQuery->query($sql5,\'RUN\');\r\n\r\n$sql4 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta-1) where id_sesi=\'$pilihan_sesi\'\";\r\n$result4= $myQuery->query($sql4,\'RUN\');\r\n\r\n 	\r\n//}\r\nif($result && $result2 && $result3 && $result4 && $result5 && $result6)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=3213&sesi=\'.$pilihan_sesi.\'&tahap_penilaian=\'.$tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n}\r\n\r\n}//eof syarat kekosongan tempat\r\n//}//eof for loop \r\n\r\n', '', '', '00', '1', '2009-10-29', '1', '2010-03-03');
INSERT INTO `flc_bl` VALUES ('30', 'JADUAL_SEMULA_INDIVIDU', 'JADUAL_SEMULA_INDIVIDU', '', 'include(\'db.php\');\r\n\r\n$sesi = $_POST[\'input_map_89_720\'];\r\n$tahap_penilaian = $_POST[\'input_map_89_5005\'];\r\n$masa_mula = $_POST[\'input_map_89_721\'];\r\n$masa_tamat = $_POST[\'input_map_89_722\'];\r\n$tarikh = $_POST[\'input_map_89_724\'];\r\n$lokasi = $_POST[\'input_map_89_9159\'];\r\n$pusat_penilaian = $_POST[\'input_map_89_726\'];\r\n$jumlah_peserta = $_POST[\'input_map_89_728\'];\r\n$id_sesi = $_POST[\'input_map_89_5214\'];\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh_kemaskini=date(\"Y-m-d\",$date1);\r\n\r\n$peserta = \"select count(id_peserta) from usr_isac.prs_permohonan where id_sesi=\'$id_sesi \' \";\r\n$pesertaRs = $myQuery->query($peserta,\'SELECT\') ;\r\n\r\n$bil_peserta = $pesertaRs[0][0];\r\n\r\n$all = \"select jumlah_keseluruhan from usr_isac.pro_sesi where id_sesi=\'$id_sesi \' \";\r\n$allRs = $myQuery->query($all,\'SELECT\') ;\r\n\r\n$jum_keseluruhan = $allRs[0][0];\r\n\r\n//$jumlah_peserta;\r\n//bilangan kekosongan tempat\r\n$bil_kekosongan = ($jumlah_peserta-$bil_peserta);\r\n\r\n$sql1 = \"update usr_isac.pro_sesi set kod_sesi_penilaian=\'$sesi\',tarikh_sesi= str_to_date(\'$tarikh\',\'%d-%m-%Y\'),kod_masa_mula=\'$masa_mula\',kod_masa_tamat=\'$masa_tamat\',bilangan_peserta=\'$bil_kekosongan\',lokasi=\'$lokasi\',kod_iac=\'$pusat_penilaian\',kod_tahap=\'$tahap_penilaian\',jumlah_keseluruhan=\'$jumlah_peserta\',tarikh_kemaskini=\'$tarikh_kemaskini\' where id_sesi = $id_sesi\";\r\n\r\n$result1 = $myQuery->query($sql1,\'RUN\');\r\n\r\nif(result1){\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=82&sesi=\'.$sesi.\'&tahap_penilaian=\'.$kod_tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}', '', '', '00', '1', '2009-10-30', '1', '2010-03-27');
INSERT INTO `flc_bl` VALUES ('29', 'SEMAK_STATUS_PESERTA_KUMPULAN', 'SEMAK_STATUS_PESERTA_KUMPULAN', '', 'include(\'db.php\');\r\n\r\n$no_kad_pengenalan=$_POST[\'input_map_6514_9172\'];\r\n$no_kad_pengenalan_lain=$_POST[\'input_map_6514_9171\'];\r\n$tahap_penilaian=$_POST[\'input_map_6514_9168\'];\r\n$pilihan_sesi=$_POST[\'input_map_6514_9170\'];\r\n//$tujuan=$_POST[\'input_map_6514_11353\'];\r\n\r\n//confirm no kad pengenalan @ no kad pengenalan lain.\r\nif (($no_kad_pengenalan != \'{POST|input_map_6514_9175}\') || ($no_kad_pengenalan_lain != \'{POST|input_map_6514_9176}\'))\r\n{\r\necho \"<script language=javascript>alert(\'No.MyKad/Polis/Tentera/Passport yang anda masukkan tidak sama, sila masukkan semula\'); </script>\";\r\n		redirect(\'index.php?page=page_wrapper&menuID=3211&tahap_penilaian=\'.$tahap_penilaian.\'&sesi=\'.$pilihan_sesi);\r\n\r\n}\r\n\r\nelse\r\n{\r\n\r\n\r\n//temp\r\nif($no_kad_pengenalan)\r\n	$extraSQL = \"b.no_kad_pengenalan=\'$no_kad_pengenalan\'\";\r\nelse if($no_kad_pengenalan_lain)\r\n	$extraSQL = \"b.no_kad_pengenalan_lain=\'$no_kad_pengenalan_lain\'\";\r\n\r\n//search active -->peserta telah daftar dlm kategori individu dan belum menduduki penilaian.\r\n$daftar=\"select a.id_peserta\r\n		from \r\n			usr_isac.prs_permohonan a, \r\n			usr_isac.pro_peserta b\r\n		where \r\n			b.kod_kategori_peserta=\'01\'\r\n			and a.kod_status_permohonan=\'01\' \r\n			and \".$extraSQL.\"\r\n			and a.id_peserta=b.id_peserta\";\r\n\r\n$daftarRs=$myQuery->query($daftar,\'SELECT\');\r\n\r\n//search active -->peserta telah daftar dlm kategori individu dan belum menduduki penilaian.\r\n$daftarK=\"select a.id_peserta\r\n		from \r\n			usr_isac.prs_permohonan a, \r\n			usr_isac.pro_peserta b\r\n		where \r\n			b.kod_kategori_peserta=\'02\'\r\n			and a.kod_status_permohonan=\'01\' \r\n			and \".$extraSQL.\"\r\n			and a.id_peserta=b.id_peserta\";\r\n\r\n$daftarKRs=$myQuery->query($daftarK,\'SELECT\');\r\n\r\n//search lulus -->peserta telah lulus penilaian basic\r\n$lulusBasic=\"select a.id_peserta\r\n			 from \r\n				usr_isac. prs_penilaian_peserta a,\r\n				usr_isac.pro_peserta b\r\n			 where \r\n				b.kod_kategori_peserta=\'02\'\r\n				and a.kod_status_kelulusan=\'Lulus\'\r\n				and \".$extraSQL.\"\r\n				and a.id_peserta=b.id_peserta\";\r\n$lulusBasicRs=$myQuery->query($lulusBasic,\'SELECT\');\r\n\r\n//search gagal -->peserta gagal, utk mohon semula perlu melepasi tempoh sebulan(kawalan sistem) dari tarikh penilaian\r\n\r\n$gagal=\"select b.id_peserta\r\n		from \r\n			usr_isac.pro_sesi a,\r\n			usr_isac.pro_peserta b,\r\n			usr_isac.pro_kawalan_sistem c,\r\n			usr_isac.prs_permohonan d,\r\n			usr_isac.prs_penilaian_peserta e\r\n		where\r\n			a.id_sesi=d.id_sesi \r\n			and d.id_peserta=b.id_peserta \r\n			and b.id_peserta=e.id_peserta\r\n			and e.kod_status_kelulusan=\'Gagal\'\r\n			and \".$extraSQL.\"\r\n			and adddate(a.tarikh_sesi,c.tempoh_kebenaran_permohonan_peserta_gagal) >= curdate()\";\r\n$gagalRs=$myQuery->query($gagal,\'SELECT\');\r\n\r\n//search blacklist -->peserta yg tidak hadir penilaian dan tidak membuat rayuan kepada admin, utk mohon msemula perlu melepasi tempoh 6 bln(kawalan sistem)\r\n\r\n$blacklist=\"select b.id_peserta\r\n		       from \r\n				usr_isac.pro_sesi a,\r\n				usr_isac.pro_peserta b,\r\n				usr_isac.pro_kawalan_sistem c,\r\n				usr_isac.prs_permohonan d,\r\n				usr_isac.prs_penilaian_peserta e\r\n			where \r\n				a.id_sesi=d.id_sesi \r\n				and d.id_peserta=b.id_peserta \r\n				and b.id_peserta=e.id_peserta\r\n				and e.kod_status_kehadiran=\'02\'\r\n				and \".$extraSQL.\"\r\n				and adddate(a.tarikh_sesi,c.tempoh_kebenaran_permohonan_peserta_blacklist) >= curdate()\";\r\n$blacklistRs=$myQuery->query($blacklist,\'SELECT\');\r\n\r\n$all = \"select b.no_kad_pengenalan,b.no_kad_pengenalan_lain from usr_isac.pro_peserta b where \".$extraSQL;\r\n\r\n$allRs = $myQuery->query($all,\'SELECT\');\r\n\r\n//if basic\r\nif($tahap_penilaian==\'01\')\r\n{\r\n\r\n	//if active, keluar\r\n	if($daftarRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, peserta ini telah dijadualkan dalam kategori individu.\'); </script>\";\r\n		$location=1;	\r\n	}\r\n	//if active, keluar\r\n	else if($daftarKRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, peserta ini telah dijadualkan dalam kategori kumpulan dan masih aktif.\'); </script>\";\r\n		$location=1;	\r\n	}\r\n	else if ($lulusBasicRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Peserta telah lulus penilaian tahap asas\'); </script>\";\r\n		$location=1;\r\n	}	\r\n	else if ($gagalRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, peserta ini tidak dibenarkan memohon semula dalam tempoh kurang daripada satu bulan daripada tarikh menduduki penilaian yang terakhir.\'); </script>\";\r\n		$location=1;\r\n	}\r\n	else if ($blacklistRs)\r\n	{\r\n		echo \"<script language=javascript>alert(\'Harap maaf, peserta ini telah disenaraihitamkan. Sila hantar rayuan melalui emel kepada isachelp@intanbk.intan.my atau peserta hanya boleh memohon semula selepas 6 bulan dari tarikh penilaian yang terakhir\'); </script>\";\r\n		$location=1;\r\n	}		\r\n  \r\n	//if not active, masuk\r\n     else if(count($allRs) < 1) \r\n	{\r\n		$location=2;\r\n	}\r\n\r\n        else{\r\n              $location=3;\r\n        }\r\n\r\n}\r\n//advance\r\nelse \r\n{\r\n        if(count($allRs) < 1) \r\n	{  \r\n              \r\n		echo \"<script language=javascript>alert(\'Maaf, peserta ini belum melepasi penilaian Tahap Asas.\'); </script>\";\r\n		$location=1;\r\n	}   \r\n \r\n}\r\n\r\n//temp2\r\nif($no_kad_pengenalan)\r\n	$extraURL = \"&no_kad_pengenalan=$no_kad_pengenalan\";\r\nelse if($no_kad_pengenalan_lain)\r\n	$extraURL = \"&no_kad_pengenalan_lain=$no_kad_pengenalan_lain\";\r\n\r\n//switch location\r\nswitch($location)\r\n{\r\n	//kembali ke carian peserta\r\n	case 1: redirect(\'index.php?page=page_wrapper&menuID=3211&tahap_penilaian=\'.$tahap_penilaian.\'&sesi=\'.$pilihan_sesi);\r\n		break;\r\n	//daftar peserta baru\r\n	case 2: redirect(\'index.php?page=page_wrapper&menuID=3210&tahap_penilaian=\'.$tahap_penilaian.\'&sesi=\'.$pilihan_sesi.$extraURL);\r\n		break;\r\n	//kemaskini maklumat peserta/daftar semula\r\n	case 3: redirect(\'index.php?page=page_wrapper&menuID=3221&tahap_penilaian=\'.$tahap_penilaian.\'&sesi=\'.$pilihan_sesi.$extraURL);\r\n		break;\r\n\r\n}\r\n\r\n} //eof confirm ic\r\n\r\n', '', '', '00', '1', '2009-10-29', '1', '2010-03-03');
INSERT INTO `flc_bl` VALUES ('31', 'PENGESAHAN_RAYUAN_PESERTA_BLACKLIST', 'PENGESAHAN_RAYUAN_PESERTA_BLACKLIST', '', 'include(\'db.php\');\r\n\r\n//prs_permohonan_rayuan\r\n$tarikh_rayuan = $_POST[\'input_map_201_1232\'];\r\n$catatan = $_POST[\'input_map_201_1231\'];\r\n$keputusan = $_POST[\'input_map_201_1152\'];\r\n//$tarikh_keputusan = $_POST[\'input_map_89_726\'];\r\n//$tarikh_cipta = $_POST[\'input_map_89_728\'];\r\n$id_peserta = $_POST[\'input_map_201_5219\'];\r\n\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n//prs_permohonan_peserta\r\n$status = $_POST[\'input_map_201_1152\'];\r\n\r\nif (!$id_peserta)\r\n{\r\n$sql1 = \"insert into usr_isac.prs_permohonan_rayuan (tarikh_rayuan,catatan_permohonan_rayuan,keputusan_permohonan_rayuan,tarikh_cipta,id_peserta) value(\'$tarikh_rayuan\',\'$catatan\',\'$keputusan\',\'$tarikh\',\'$id_peserta\')\";\r\n$result1 = $myQuery->query($sql1,\'RUN\');\r\n}\r\nelse\r\n{\r\n$sql1 = \"update usr_isac.prs_permohonan_rayuan set tarikh_rayuan=\'$tarikh_rayuan\',catatan_permohonan_rayuan=\'$catatan\',keputusan_permohonan_rayuan=\'$keputusan\',tarikh_cipta=\'$tarikh\'  where id_peserta = \'$id_peserta\'\";\r\n$result1 = $myQuery->query($sql1,\'RUN\');\r\n}\r\n\r\n$sql2 = \"update usr_isac.prs_permohonan_peserta set kod_status=\'$status\' where id_peserta = \'$id_peserta\' \";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\nif(result1 && result2){\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=138\');\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n}', '', '', '00', '1', '2009-11-02', '1', '2010-03-01');
INSERT INTO `flc_bl` VALUES ('42', 'KEMASKINI_MAKLUMAT_PESERTA_KUMPULAN', 'KEMASKINI_MAKLUMAT_PESERTA_KUMPULAN', '', 'include(\'db.php\');\r\n\r\n//pro_peserta\r\n$id=$_POST[\'input_map_3347_5280\'];\r\n$ic = $_POST[\'input_map_3347_5276\'];\r\n$ic_lain = $_POST[\'input_map_3347_5277\'];\r\n$nama_peserta = $_POST[\'input_map_3347_5275\'];\r\n\r\n//pro_perkhidmatan\r\n$kod_klasifikasi_perkhidmatan = $_POST[\'input_map_3347_5278\'];\r\n$kod_gred_jawatan = $_POST[\'input_map_3347_5279\'];\r\n$tujuan = $_POST[\'input_map_3347_11360\'];\r\n\r\n$sql = \"update usr_isac.pro_peserta set\r\nnama_peserta=\'$nama_peserta\',no_kad_pengenalan=\'$ic\',no_kad_pengenalan_lain=\'$ic_lain\' where id_peserta=\'$id\'\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_perkhidmatan set\r\nkod_klasifikasi_perkhidmatan=\'$kod_klasifikasi_perkhidmatan\',kod_gred_jawatan=\'$kod_gred_jawatan\' where id_peserta=\'$id\'\";\r\n\r\n\r\n$sql3 = \"update usr_isac.prs_permohonan set kod_tujuan=\'$tujuan\' where id_peserta=\'$id\'\";\r\n\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\nif($result && $result2 && $result3)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\n\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}', '', '', '00', '1', '2009-11-10', '1', '2010-03-04');
INSERT INTO `flc_bl` VALUES ('32', 'DAFTAR_KUMPULAN_PILIHAN_SESI_PENILAIAN', 'DAFTAR_KUMPULAN_PILIHAN_SESI_PENILAIAN', '', 'include(\'db.php\');\r\n\r\n//prs permohonan\r\n$tahap = $_POST[\'input_map_3331_5204\'];\r\n$pilihan_sesi = $_POST[\'input_map_3331_5211\'];\r\n\r\n$j = $_POST[\'input_map_3331_5211\'];\r\nfor($x = 0; $x < count($j); $x++)\r\n{\r\n$sql = \"insert into usr_isac.prs_permohonan\r\n(id_penyelaras,tarikh_permohonan,kod_tahap,id_sesi)\r\nvalues \r\n(\'$id_peserta\',\'$tarikh\',\'$tahap\',\'$pilihan_sesi[$x]\')\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta-1) where id_sesi=\'$pilihan_sesi[$x]\'\";\r\n$result2= $myQuery->query($sql2,\'RUN\');\r\n\r\n}\r\n\r\nif($result)\r\n{\r\n\r\n$sesi=\"select b.id_sesi,b.kod_tahap \r\n           from \r\n                usr_isac.pro_penyelaras a,\r\n                usr_isac.prs_permohonan b,\r\n                usr_isac.pro_sesi c \r\n           where\r\n                a.id_penyelaras = b.id_penyelaras and \r\n                b.id_sesi = c.id_sesi and (a.log_id_pengguna=\'{SESSION|publicID}\')\";\r\n\r\n$sesiRs=$myQuery->query($sesi,\'SELECT\');\r\n\r\n       $sesi = $sesiRs[0][0];\r\n       $kod_tahap = $sesiRs[0][1];\r\n\r\n\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=35&sesi=\'.$sesi.\'&tahap_penilaian=\'.$kod_tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}\r\n', '', '', '00', '1', '2009-11-03', '1', '2009-11-03');
INSERT INTO `flc_bl` VALUES ('33', 'PILIHAN_SESI_PENILAIAN', 'PILIHAN_SESI_PENILAIAN', '', '$pilihan_sesi = $_POST[\'input_map_3315_5067\'];\r\n$tahap_penilaian = $_POST[\'input_map_3314_5050\'];\r\n//$tujuan = $_POST[\'input_map_3314_5049\'];\r\n\r\nfor($x=0;$x<count($_POST[\'input_map_3315_5067\']);$x++)\r\n{\r\n$pilihan_sesi = $_POST[\'input_map_3315_5067\'][$x];\r\n\r\n}\r\nredirect(\'index.php?page=page_wrapper&menuID=3211&tahap_penilaian=\'.$tahap_penilaian.\'&sesi=\'.$pilihan_sesi);\r\n\r\n', '', '', '00', '1', '2009-11-03', '1', '2010-03-03');
INSERT INTO `flc_bl` VALUES ('34', 'KEMASUKAN_BANK_SOALAN_KEMAHIRAN', 'KEMASUKAN_BANK_SOALAN_KEMAHIRAN', '', 'include(\'db.php\');\r\n\r\n$kod_tahap_soalan = $_POST[\'input_map_3336_5242\'];\r\n$kod_set_soalan = $_POST[\'input_map_3336_5243\'];\r\n$arahan_umum = $_POST[\'input_map_3336_5244\'];\r\n$kod_bahagian_soalan = $_POST[\'input_map_3336_5246\'];\r\n$kod_bentuk_soalan = $_POST[\'input_map_3336_5247\'];\r\n$arahan_soalan= $_POST[\'input_map_3336_5245\'];\r\n$id_soalan= $_POST[\'input_map_3336_5290\'];\r\n$id_pengguna = $_POST[\'input_map_3336_5291\'];\r\n$upload=upload_file(\'./upload/\',$_FILES[\'input_map_3336_8938\']);\r\n\r\n$id_kemahiran = $_POST[\'input_map_3337_9063\'];\r\n\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n$sql3 = \"insert into usr_isac.pro_kemahiran (KOD_SET_SOALAN,ARAHAN_UMUM,KOD_BAHAGIAN_SOALAN,KOD_BENTUK_SOALAN) VALUES\r\n(\'$kod_set_soalan\',\'$arahan_umum\',\'$kod_bahagian_soalan\',\'$kod_bentuk_soalan\')\";\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\nfor($x=0;$x<count($_POST[\'input_map_3337_5248\']);$x++){\r\n\r\n$soalan = $_POST[\'input_map_3337_5248\'][$x];\r\n$jawapan = $_POST[\'input_map_3337_5249\'][$x];\r\n$markah = $_POST[\'input_map_3337_5250\'][$x];\r\n//$status = $_POST[\'input_map_3336_8752\'];\r\n\r\n\r\n$sql = \"insert into usr_isac.pro_soalan (PENYATAAN_SOALAN,KOD_TAHAP_SOALAN,MUAT_NAIK_FAIL,ARAHAN_SOALAN,TARIKH_CIPTA,KOD_STATUS,ID_PENGGUNA,ID_KEMAHIRAN) VALUES\r\n(\'$soalan\',\'$kod_tahap_soalan\',\'$upload\',\'$arahan_soalan\',\'$tarikh\',\'02\',\'$id_pengguna\',(select max(id_kemahiran) from usr_isac.pro_kemahiran))\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"insert into usr_isac.pro_jawapan(id_soalan,skema_jawapan,markah) values\r\n((select max(id_soalan) from usr_isac.pro_soalan),\'$jawapan\',\'$markah\')\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n$sql4 = \"select max(id_soalan) from usr_isac.pro_soalan\";\r\n$result4 =  $myQuery->query($sql4,\'SELECT\');\r\n\r\n$id_soalan=$result4[0][0];\r\n\r\n}\r\n\r\n\r\nif($result && $result2 && $result3){\r\n//redirect(\'index.php?page=page_wrapper&menuID=3231&keyid=\'.$kod_set_soalan.\'\');\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=3231&keyid=\'.$kod_set_soalan);\r\n\r\n\r\n}\r\nelse{\r\n\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n\r\n}\r\n\r\n', '', '', '00', '1', '2009-11-04', '1', '2009-12-31');
INSERT INTO `flc_bl` VALUES ('35', 'KEMASKINI_DAFTAR_PESERTA_KUMPULAN', 'KEMASKINI_DAFTAR_PESERTA_KUMPULAN', '', 'include(\'db.php\');\r\n\r\n//pro_peserta\r\n$id=$_POST[\'input_map_3355_5312\'];\r\n$nama_peserta = $_POST[\'input_map_3355_5313\'];\r\n$id_penyelaras = $_POST[\'input_map_3355_8650\'];\r\n\r\n//pro_perkhidmatan\r\n$kod_klasifikasi_perkhidmatan = $_POST[\'input_map_3355_5316\'];\r\n$kod_gred_jawatan = $_POST[\'input_map_3355_5317\'];\r\n\r\n//prs_permohonan\r\n$pilihan_sesi = $_POST[\'input_map_3355_8642\'];\r\n$tahap = $_POST[\'input_map_3355_8641\'];\r\n//$tujuan = $_POST[\'input_map_3355_8644\'];\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n$sql = \"update usr_isac.pro_peserta set nama_peserta=\'$nama_peserta\',id_penyelaras=\'$id_penyelaras\' where id_peserta=\'$id\'\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_perkhidmatan set kod_klasifikasi_perkhidmatan=\'$kod_klasifikasi_perkhidmatan\',kod_gred_jawatan=\'$kod_gred_jawatan\' where id_peserta=\'$id\'\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n$sql3 = \"insert into usr_isac.prs_permohonan\r\n(id_penyelaras,id_peserta,kod_status_permohonan,kod_status_kehadiran,tarikh_permohonan,kod_tahap,kod_tujuan,id_sesi)\r\nvalues \r\n(\'$id_penyelaras\',\'$id\',\'01\',\'02\',\'$tarikh\',\'$tahap\',\'04\',\'$pilihan_sesi\')\";\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n$sql4 = \"insert into usr_isac.prs_permohonan_peserta\r\n(id_peserta,id_permohonan)\r\nvalues \r\n(\'$id\',(select max(id_permohonan) from usr_isac.prs_permohonan))\";\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\n$sql5 = \"update usr_isac.pro_sesi set bilangan_peserta = (bilangan_peserta-1) where id_sesi=\'$pilihan_sesi\'\";\r\n$result5= $myQuery->query($sql5,\'RUN\');\r\n\r\nif($result && $result2 && $result3 && $result4 && $result5)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=3213&sesi=\'.$pilihan_sesi.\'&tahap_penilaian=\'.$tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}', '', '', '00', '36', '2009-11-06', '1', '2010-02-24');
