INSERT INTO `flc_bl` VALUES ('48', 'NOTIFIKASI_EMAIL_PERMOHONAN_PERNILAIAN_PESERTA', 'NOTIFIKASI_EMAIL_PERMOHONAN_PERNILAIAN_PESERTA', 'created by safa\r\nto test email notification for immediate mode.', '$namaPemohon= $_POST[\'input_map_6387_8470\'];\r\n$namaPenilaian= $_POST[\'input_map_6387_8471\'];\r\n$tarikh= $_POST[\'input_map_6387_8472\'];\r\n$lokasi= $_POST[\'input_map_6387_8473\'];\r\n\r\n$txtMsg=\"Adalah dimaklumkan bahawa permohonan tuan/puan untuk  menduduki ujian Penilaian IT Skill Assessment and Certification (ISAC) telah diterima.  \r\nMaklumat permohonan adalah seperti berikut :-\r\n\".\" \".\"\r\nNama Pemohon :\".\" \".$namaPemohon.\" \".\"\r\nNama Penilaian :\".\" \".$namaPenilaian.\" \".\"\r\nTarikh : \".\" \".$tarikh.\" \".\"\r\nLokasi :\".\" \".$lokasi;\r\n\r\n// immediate\r\nnotification(\'NOTIFIKASI_EMAIL_PERMOHONAN_PERNILAIAN_PESERTA\',\"EMAIL_TO=\'safa_shy86@yahoo.com,safarinabadrun@yahoo.com.my\'\",\"EMAIL_FROM=\'safarina.badrun@gmail.com\'\",\"EMAIL_SUBJECT=\'Permohonan Penilaian IT Skill Assessment and Certification (ISAC) \'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EMAIL_MESSAGE=\'\".$txtMsg.\"\'\",\"EXECUTION_DATE=curdate()\");\r\n\r\n', '', '', '00', '1', '2009-11-15', '1', '2009-11-27');
INSERT INTO `flc_bl` VALUES ('49', 'DAFTAR_PENYELARAS', 'Daftar_Penyelaras', '', 'include(\'db.php\');\r\n\r\n$added_by = $_SESSION[\'userID\'];\r\n$id = $_POST[\'input_map_6407_8564\']; //hidden\r\n$nama= $_POST[\'input_map_6407_8562\']; \r\n$username = $_POST[\'input_map_6407_8565\'];\r\n$password = md5($_POST[\'input_map_6407_8566\']);\r\n$katalaluan = $_POST[\'input_map_6407_8566\'];\r\n$emel= $_POST[\'input_map_6407_8563\'];\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n$kod_kementerian= $_POST[\'input_map_6407_8567\'];\r\n$kumpulan_pengguna =\'4\';\r\n\r\n//check log_id_pengguna\r\n$checkid = \"select log_id_pengguna from usr_isac.pro_penyelaras where log_id_pengguna=\'$username\' \";\r\n$checkidRs = $myQuery->query($checkid,\'SELECT\');\r\n//$uname = $checkidRs[0][0];\r\n$uname = \"select username from pruser where username=\'$username\' \";\r\n$unameRs = $myQuery->query($uname,\'SELECT\');\r\n\r\nif($unameRs != 0||$checkidRs != 0)\r\n{	\r\n       echo \"<script language=javascript>alert(\'Id Pengguna yang dimasukkan telah wujud. Sila masukkan Id Pengguna yang lain.\'); </script>\";\r\n redirect(\'index.php?page=page_wrapper&menuID=6247\');\r\n}\r\n\r\nelse\r\n{\r\n\r\n$sql = \"insert into PRUSER (name,username,userpassword,email,createddate) \r\nvalues (\'$nama\',\'$username\',\'$password\',\'$emel\',\'$tarikh\')\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"insert into flc_user_group_mapping(group_id,user_id,added_by,added_date) \r\nvalues (\'$kumpulan_pengguna\',(select max(userid) from pruser),\'$added_by\',\'$tarikh\')\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n//penyelaras\r\n$sql3 = \"insert into usr_isac.pro_penyelaras (nama_penyelaras,emel_penyelaras,kod_kementerian,log_id_pengguna,katalaluan,userid)\r\nvalues(\'$nama\',\'$emel\',\'$kod_kementerian\',\'$username\',\'$katalaluan\',(select max(userid) from pruser)) \";\r\n\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n$sql4 = \"insert into usr_isac.pro_tempat_tugas (id_penyelaras)\r\nvalues((select max(id_penyelaras) from usr_isac.pro_penyelaras)) \";\r\n\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\n\r\n$txtMsg=\'<html>\r\n<head>\r\n<style type=\"text/css\">\r\n<!--\r\n.style30 {font-size: 14px;}\r\n.style53 {font-family: Arial, Helvetica, sans-serif; font-size: 16;}\r\n.style54 {font-family: Arial, Helvetica, sans-serif; font-size: 16; font-weight: bold; }\r\n-->\r\n</style>\r\n</head>\r\n\r\n<body>\r\n<table width=\"847\" border=\"0\" cellpadding=\"0\" cellspacing=\"3\" class=\"style30\">\r\n  <tr class=\"style30\">\r\n    <td><span class=\"style53\">Tuan / Puan,\r\n      </span>\r\n      <p align=\"justify\" class=\"style53\"><br />\r\n            <br />\r\n    </p></td>\r\n  </tr>\r\n  <tr class=\"style30\">\r\n    <td class=\"style53\">Pendaftaran anda untuk permohonan ISAC telah diterima. Berikut merupakan maklumat id dan kata laluan untuk kemasukan pendaftaran peserta.</td>\r\n  </tr>\r\n  <tr class=\"style30\">\r\n    <td> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"><table width=\"532\" border=\"0\" align=\"center\">\r\n      <tr>\r\n        <td width=\"252\" class=\"style54\">id penyelaras</td>\r\n        <td width=\"10\" class=\"style54\">:</td>\r\n        <td width=\"260\" class=\"style54\">\';\r\n		$txtMsg.= $username;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"style54\">kata laluan</td>\r\n        <td class=\"style54\">:</td>\r\n        <td class=\"style54\">\';\r\n		$txtMsg.=$katalaluan;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n    </table></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>Sebarang kemusykilan/masalah, sila hubungi kami melalui emel:  <a href=\"mailto:isachelp@intanbk.intan.my\">isachelp@intanbk.intan.my</a>.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>Sekian, terima  kasih.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>Pentadbir  ISAC<br />\r\n      Institut  Tadbiran Awam Negara<br />\r\n      Jabatan  Perkhidmatan Awam, Malaysia</p></td>\r\n  </tr>\r\n</table>\r\n</body>\r\n</html>\';\r\n// send mail to penyelaras\r\nnotification(\'NOTIFIKASI ID & USERNAME KEPADA PENYELARAS\',\"REFFERENCE_ID=\'\".$username.\"\'\",\"EMAIL_TO=\'\".$emel.\"\'\",\"EMAIL_CC=\'intan.isac@gmail.com\'\",\"EMAIL_FROM=\'isacadmin@intanbk.intan.my\'\",\"EMAIL_SUBJECT=\'LOGIN ID & KATA LALUAN PERMOHONAN ISAC (KUMPULAN)\'\",\"EMAIL_MESSAGE=\'\".$txtMsg.\"\'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EXECUTION_DATE=curdate()\");\r\nif($result && $result2 && $result3 && $result4){\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\n}\r\nelse{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n}\r\n\r\n}', '', '', '00', '1', '2009-11-19', '1', '2010-03-27');
