INSERT INTO `flc_bl` VALUES ('57', 'PEMILIHAN_SOALAN_RANDOM', 'PEMILIHAN_SOALAN_RANDOM', '', 'include (\'db.php\');\r\n//$e = $_POST[\'input_map_6423_8661\'];//count soalan\r\n$kategori = $_POST[\'input_map_6423_8655\'];//kategori_soalan\r\n$j= $_POST[\'input_map_6423_8656\'];//jumlah_soalan\r\n$tahap = $_POST[\'input_map_6423_8654\'];\r\n$id_pemilihan_soalan = $_POST[\'input_map_6424_8941\'];\r\n$id_peserta = $_POST[\'input_map_6424_8942\'];\r\n\r\n//echo  $id_peserta;\r\n$mohon =  \"select id_permohonan,kod_tahap from usr_isac.prs_permohonan where id_permohonan=(select max(id_permohonan) from usr_isac.prs_permohonan where id_peserta=$id_peserta)\";\r\n$mohonRs = $myQuery->query($mohon,\'SELECT\');\r\n$id_mohon = $mohonRs[0][0];\r\n$kod_tahap = $mohonRs[0][1];\r\n//echo $id_mohon;\r\n\r\n$sql_jumk= \"select jumlah_keseluruhan_soalan from usr_isac.pro_pemilihan_soalan where kod_tahap_soalan=\'$kod_tahap\' \";\r\n$result_jumk = $myQuery->query($sql_jumk,\'SELECT\');\r\n\r\n$g=mysql_query(\"select kod_tahap_soalan,kod_kategori_soalan,nilai_jumlah_soalan from usr_isac.pro_pemilihan_soalan_kumpulan where id_pemilihan_soalan=(select id_pemilihan_soalan from usr_isac.pro_pemilihan_soalan where kod_tahap_soalan = \'$kod_tahap\')\");\r\nwhile($row = mysql_fetch_array($g))\r\n {\r\n$tahap = $row[kod_tahap_soalan];\r\n$kategori = $row[kod_kategori_soalan];\r\n$jum = $row[nilai_jumlah_soalan];\r\n\r\n$gRs2 = array();\r\n$g2=mysql_query(\"select id_soalan from usr_isac.pro_soalan where kod_tahap_soalan=\'$tahap\' and kod_kategori_soalan=\'$kategori\' and kod_status=\'02\' \");\r\nwhile($row = mysql_fetch_array($g2))\r\n {\r\n $gRs2[] = $row[id_soalan];\r\n }\r\n\r\n$random = array_rand($gRs2,$jum);\r\nforeach($random as $key => $value)\r\n      {\r\n$random_soalan = $gRs2[$value];\r\n\r\n$sql_check = \"select * from usr_isac.pro_pemilihan_soalan_perincian where id_peserta = \'$id_peserta\'  and id_permohonan = \'$id_mohon\' and id_pemilihan_soalan = (select id_pemilihan_soalan from usr_isac.pro_pemilihan_soalan where kod_tahap_soalan=\'$kod_tahap\')\";\r\n$result_check = $myQuery->query($sql_check,\'SELECT\');\r\n\r\nif(count($result_check) < $result_jumk[0][0]){\r\n\r\n$sql = \"insert into usr_isac.pro_pemilihan_soalan_perincian (ID_PEMILIHAN_SOALAN,ID_SOALAN,ID_PESERTA,ID_PERMOHONAN,STATUS_PUSH_PULL) VALUES\r\n(\'$id_pemilihan_soalan\',\'$gRs2[$value]\',\'$id_peserta\',\'$id_mohon\',\'I\')\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n}\r\n     }\r\n}\r\n\r\n//$id_peserta = $_POST[\'input_map_6493_9058\'];\r\n$jum = 1;\r\n\r\n$x=mysql_query(\"Select distinct(kod_set_soalan) from usr_isac.pro_kemahiran where kod_set_soalan=(select min(kod_set_soalan) from usr_isac.pro_kemahiran)\");\r\nwhile($row = mysql_fetch_array($x))\r\n  {\r\n  $xRs=$row[kod_set_soalan];\r\n  }\r\n\r\n$y=mysql_query(\"Select distinct(kod_set_soalan) from usr_isac.pro_kemahiran where kod_set_soalan=(select max(kod_set_soalan) from usr_isac.pro_kemahiran)\");\r\nwhile($row = mysql_fetch_array($y))\r\n  {\r\n  $yRs=$row[kod_set_soalan];\r\n  }\r\n\r\nfor ($z=0; $z<1; $z++)\r\n{\r\n	//random\r\n	$temp=rand($xRs,$yRs);\r\n        $temp;\r\n	\r\n	\r\n			$sql_check = \"select * from usr_isac.pro_pemilihan_set_kemahiran where id_peserta = \'$id_peserta\' and id_permohonan=\'$id_mohon\' \";\r\n			$result_check = $myQuery->query($sql_check,\'SELECT\');\r\n			\r\n			if(count($result_check) < $jum)\r\n			{\r\n			\r\n			$sql = \"insert into usr_isac.pro_pemilihan_set_kemahiran (KOD_SET_SOALAN,ID_PESERTA,ID_PERMOHONAN,STATUS_PUSH_PULL) values (\'$temp\',\'$id_peserta\',\'$id_mohon\',\'I\')\";\r\n			$result = $myQuery->query($sql,\'RUN\');\r\n			\r\n			}//eof if\r\n\r\n}//eof for\r\n', '', '', '00', '1', '2009-11-23', '1', '2010-05-03');
