INSERT INTO `flc_bl` VALUES ('61', 'KEMASUKAN_BARU_JADUAL_INDIVIDU', 'KEMASUKAN_BARU_JADUAL_INDIVIDU', '', 'include(\'db.php\');\r\n\r\n//$added_by = $_SESSION[\'userID\'];\r\n$sesi = $_POST[\'input_map_82_633\'];\r\n$tahap_penilaian= $_POST[\'input_map_82_4911\'];\r\n$masa_mula = $_POST[\'input_map_82_634\'];\r\n$masa_tamat = $_POST[\'input_map_82_653\'];\r\n$tarikh_penilaian = $_POST[\'input_map_82_655\'];\r\n$lokasi = $_POST[\'input_map_82_9136\'];\r\n$pusat_penilaian = $_POST[\'input_map_82_657\'];\r\n$jumlah_peserta = $_POST[\'input_map_82_659\'];\r\n$kod_kategori_peserta = $_POST[\'input_map_82_5220\'];  	\r\n$id_pengguna = $_POST[\'input_map_82_9185\'];\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n$tarikh_mula = $_POST[\'input_map_82_9163\'];\r\n$tarikh_tamat = $_POST[\'input_map_82_9162\'];\r\n\r\n\r\n$bezahari = mysql_query(\"SELECT DATEDIFF(str_to_date(\'\".$tarikh_tamat.\"\',\'%d-%m-%Y\'),str_to_date(\'\".$tarikh_mula.\"\',\'%d-%m-%Y\')) AS diffdate\");\r\nwhile($row = mysql_fetch_array($bezahari))\r\n{\r\n\r\n$bilhari =$row[diffdate];\r\n\r\n}\r\n\r\nfor($xr=0;$xr<=$bilhari;$xr++){\r\n\r\n$tarikh_temp = strtotime ( \'+\'.$xr.\' day\' , strtotime ($tarikh_mula ) ) ;\r\n$tarikh_temp1 = date ( \'d-m-Y\' , $tarikh_temp);\r\n\r\n\r\n$j = 10000;\r\n$k = 99999;\r\nfor ($f=0; $f<1; $f++)\r\n{\r\n//random\r\n$temp=rand($j,$k);\r\n//echo \'~~\';\r\n$b=mysql_query(\"Select kod_pengesahan_penilaian from usr_isac.pro_sesi\");\r\nwhile($row = mysql_fetch_array($b))\r\n  {\r\n  \r\n  if($bRs==$temp )\r\n   {\r\n    //re-random\r\n    echo $temp=rand($j,$k);\r\n    }\r\n }\r\n}//eof for\r\n\r\n$temp;\r\n\r\n$sql = \"insert into usr_isac.pro_sesi(kod_sesi_penilaian,tarikh_sesi,kod_masa_mula,kod_masa_tamat,bilangan_peserta,lokasi,kod_iac,kod_tahap,kod_kategori_peserta,kod_pengesahan_penilaian,jumlah_keseluruhan,tarikh_cipta,id_pengguna) values (\'$sesi\',str_to_date(\'$tarikh_temp1\',\'%d-%m-%Y\'),\'$masa_mula\',\'$masa_tamat\',\'$jumlah_peserta\',\'$lokasi\',\'$pusat_penilaian\',\'$tahap_penilaian\',\'$kod_kategori_peserta\',\'$temp\',\'$jumlah_peserta\',\'$tarikh\',\'$id_pengguna\')\";\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"insert into usr_isac.prs_penilaian(tarikh_penilaian,id_sesi) \r\nvalues (str_to_date(\'$tarikh_temp1\',\'%d-%m-%Y\'),(select max(id_sesi)from usr_isac.pro_sesi))\";\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n}\r\n\r\nif($result && $result2)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\n}\r\nelse{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n}', '', '', '00', '1', '2009-12-17', '1', '2010-01-28');
INSERT INTO `flc_bl` VALUES ('74', 'HANTAR_NOTIFIKASI_KE_IAC_INDIVIDU', 'hantar_notifikasi_ke_iac', 'send alert to penyelaras iac for exam facilities.', '\r\ninclude(\'db.php\');\r\n\r\n$iac = $_POST[\'input_map_8539_11309\'];\r\n$tahun = $_POST[\'input_map_8539_11310\'];\r\n$bulan = $_POST[\'input_map_8539_11311\'];\r\n\r\n$ref_id = $iac.\'-\'.$tahun.\'-\'.$bulan.\'-01\';\r\necho $ref_id;\r\n\r\n\r\n$sqlIac = \"SELECT\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'IAC\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=\'\".$iac.\"\') as \'IAC\'\";\r\n$sqlIacRs = $myQuery->query($sqlIac,\'SELECT\',\'NAME\');\r\n$nama_iac = $sqlIacRs[0][\'IAC\'];\r\n\r\n$sqlSenaraiJadual=\"SELECT\r\ndate_format(tarikh_sesi,\'%d/%m/%Y\') as \'TARIKH\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_sesi_penilaian) as \'SESI\', \r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_tamat)) as \'MASA\',\r\nlokasi as \'LOKASI\',\r\njumlah_keseluruhan as \'JUMLAH PESERTA\'\r\n\r\nFROM\r\nusr_isac.pro_sesi\r\n\r\nWHERE \r\nkod_kategori_peserta=\'01\' \r\nand kod_iac = \'\".$iac.\"\' \r\nand year(tarikh_sesi)=\'\".$tahun.\"\'\r\nand month(tarikh_sesi)=\'\".$bulan.\"\'\r\n\r\nORDER BY\r\ntarikh_sesi ASC,kod_sesi_penilaian ASC\";\r\n\r\n$sqlSenaraiJadualRs = $myQuery->query($sqlSenaraiJadual,\'SELECT\',\'NAME\');\r\n$countRowJadual = count($sqlSenaraiJadualRs);\r\n\r\n$sqlTarikh = \"SELECT\r\nmin(date_format(tarikh_sesi,\'%d/%m/%Y\')) as \'TARIKH DARI\',\r\nmax(date_format(tarikh_sesi,\'%d/%m/%Y\')) as \'TARIKH HINGGA\',\r\ndate_add(min(date_format(tarikh_sesi,\'%Y/%m/%d\')), interval -7 day) as \'TARIKH EMEL\'\r\nFROM\r\nusr_isac.pro_sesi\r\n\r\nWHERE \r\nkod_kategori_peserta=\'01\' \r\nand kod_iac = \'\".$iac.\"\' \r\nand year(tarikh_sesi)=\'\".$tahun.\"\'\r\nand month(tarikh_sesi)=\'\".$bulan.\"\'\";\r\n\r\n$sqlTarikhRs = $myQuery->query($sqlTarikh,\'SELECT\',\'NAME\');\r\n$tarikh_dari = $sqlTarikhRs[0][\'TARIKH DARI\'];\r\n$tarikh_hingga = $sqlTarikhRs[0][\'TARIKH HINGGA\'];\r\n$tarikh_emel = $sqlTarikhRs[0][\'TARIKH EMEL\'];\r\n\r\n$sqlEmail = \"SELECT description1 as \'EMEL_PENYELIA_KAMPUS\', description2 as \'EMEL_PENGARAH_KAMPUS\' FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'EMAIL_IAC\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=\'\".$iac.\"\'\";\r\n$sqlEmailRs = $myQuery->query($sqlEmail,\'SELECT\',\'NAME\');\r\n$emel_penyelia = $sqlEmailRs[0][\'EMEL_PENYELIA_KAMPUS\'];\r\n$emel_pengarah = $sqlEmailRs[0][\'EMEL_PENGARAH_KAMPUS\'];\r\n\r\n$txtMsg=\'<html>\r\n<head>\r\n<style type=\"text/css\">\r\n<!--\r\n.style30 {font-size: 14px;}\r\n.style53 {font-family: Arial, Helvetica, sans-serif; font-size: 16;}\r\n.style54 {font-family: Arial, Helvetica, sans-serif; font-size: 16; font-weight: bold; }\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<table width=\"847\" border=\"0\" cellpadding=\"0\" cellspacing=\"3\" class=\"style30\">\r\n  <tr class=\"style30\">\r\n    <td><span class=\"style53\"><br>      \r\n    Dengan hormatnya perkara di atas dirujuk.</span><br></td>\r\n  </tr>\r\n  <tr class=\"style30\">\r\n    <td> \r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p align=\"justify\" class=\"style53\">2. Sukacita dimaklumkan bahawa Penilaian IT Skill Assessment and Certification (ISAC) untuk individu akan diadakan seperti berikut :-</p>    </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\"><table width=\"576\" border=\"0\" align=\"center\">\r\n      <tr>\r\n        <td width=\"163\" class=\"style54\">Tarikh</td>\r\n        <td width=\"13\" class=\"style54\">:</td>\r\n        <td width=\"386\" class=\"style54\">\';\r\n		$txtMsg.=$tarikh_dari;\r\n		$txtMsg.= \' Hingga \';\r\n		$txtMsg.=$tarikh_hingga;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"style54\">Bilangan Sesi</td>\r\n        <td class=\"style54\">:</td>\r\n        <td class=\"style54\">\';\r\n		$txtMsg.=$countRowJadual;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n	   <tr>\r\n        <td class=\"style54\">Lokasi (IAC)</td>\r\n        <td class=\"style54\">:</td>\r\n        <td class=\"style54\">\';\r\n		$txtMsg.=$nama_iac;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n    </table></td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>3. Berikut merupakan maklumat jadual penialaian :</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n   <tr>\r\n    <td height=\"16\"><table width=\"838\" border=\"1\">\r\n      <tr>\r\n        <td width=\"134\" class=\"style54\">Tarikh</td>\r\n        <td width=\"179\" class=\"style54\">Sesi</td>\r\n        <td width=\"157\" class=\"style54\">Masa</td>\r\n        <td width=\"144\" class=\"style54\">Jumlah Peserta</td>\r\n      </tr>\';\r\n      \r\n      for($i=0; $i<$countRowJadual; $i++)\r\n	  {\r\n	  $txtMsg.=\'<tr> <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'TARIKH\'];\r\n	  $txtMsg.=\'</td>\r\n        <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'SESI\'];\r\n	  $txtMsg.=\'</td>\r\n        <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'MASA\'];\r\n	  $txtMsg.=\'</td>\r\n        <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'JUMLAH PESERTA\'];\r\n	  $txtMag.=\'</td>\r\n      </tr>\';\r\n      }\r\n      $txtMsg.=\'\r\n    </table></td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>4. Sehubungan dengan itu, sukacita sekiranya pihak tuan dapat memastikan pelayan ISAC di IAC anda berjalan dengan baik untuk tujuan tersebut di atas.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"> </td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\" class=\"style53\">5. Kerjasama daripada pihak tuan dalam perkara di atas didahului dengan ucapan ribuan terima kasih.</td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p> </p></td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\" class=\"style53\">Sekian.</td>\r\n  </tr>\r\n</table>\r\n</body>\r\n</html>\';\r\n\r\nnotification(\'NOTIFIKASI_FASILITI_PENILAIAN\',\"EMAIL_TO=\'\".$emel_penyelia.\"\'\",\"EMAIL_CC=\'\".$emel_pengarah.\"\'\",\"EMAIL_FROM=\'isacadmin@intanbk.intan.my\'\",\"EMAIL_SUBJECT=\'NOTIS FASILITI SERVER ISAC / MAKMAL\'\",\"EMAIL_MESSAGE=\'\".$txtMsg.\"\'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EXECUTION_DATE=\'\".$tarikh_emel.\"\'\",\"PROGRAM_TYPE=\'EMEL\'\", \"REFFERENCE_ID=\'\".$ref_id.\"\'\"); ', '', '', '00', '1', '2010-01-28', '1', '2010-03-02');
INSERT INTO `flc_bl` VALUES ('58', 'KEMASKINI_MAKLUMAT_PESERTA_PENILAIAN', 'KEMASKINI_MAKLUMAT_PESERTA_PENILAIAN', '', 'include(\'db.php\');\r\n\r\n//pro_peserta\r\n$id=$_POST[\'input_map_169_8685\'];\r\n$nama_peserta = $_POST[\'input_map_169_1059\'];\r\n\r\n//pro_perkhidmatan\r\n$kod_klasifikasi_perkhidmatan = $_POST[\'input_map_169_1062\'];\r\n$kod_gred_jawatan = $_POST[\'input_map_169_1063\'];\r\n\r\n$sql = \"update usr_isac.pro_peserta set\r\nnama_peserta=\'$nama_peserta\',status_push_pull=\'U\' where id_peserta=\'$id\'\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"update usr_isac.pro_perkhidmatan set\r\nkod_klasifikasi_perkhidmatan=\'$kod_klasifikasi_perkhidmatan\',kod_gred_jawatan=\'$kod_gred_jawatan\',status_push_pull=\'U\' where id_peserta=\'$id\'\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\nif($result && $result2)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\n\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n}', '', '', '00', '1', '2009-12-01', '1', '2010-04-12');
