ery->query($sql3,\'RUN\');\r\n\r\n}\r\n\r\n$sql4 = \"update usr_isac.prs_permohonan set\r\ntarikh_kemaskini_permohonan=\'$tarikh\',kod_tujuan=\'$tujuan\' where id_peserta=\'$id_peserta\'\";\r\n\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\nif($result && $result2 && $result3 && $result4)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Dikemaskini</div>\";\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Dikemaskini</div>\";\r\n\r\n}', '', '', '00', '1', '2010-01-06', '1', '2010-03-12');
