INSERT INTO `flc_bl` VALUES ('75', 'HANTAR_NOTIS_FASILITI_KUMPULAN', 'hantar_notis_fasiliti_kumpulan', '', 'include(\'db.php\');\r\n\r\n$iac = $_POST[\'input_map_8542_11313\'];\r\n$tahun = $_POST[\'input_map_8542_11314\'];\r\n$bulan = $_POST[\'input_map_8542_11315\'];\r\n\r\n$ref_id = $iac.\'-\'.$tahun.\'-\'.$bulan.\'-02\';\r\necho $ref_id;\r\n\r\n\r\n$sqlIac = \"SELECT\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'IAC\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=\'\".$iac.\"\') as \'IAC\'\";\r\n$sqlIacRs = $myQuery->query($sqlIac,\'SELECT\',\'NAME\');\r\n$nama_iac = $sqlIacRs[0][\'IAC\'];\r\n\r\n$sqlSenaraiJadual=\"SELECT\r\ndate_format(tarikh_sesi,\'%d/%m/%Y\') as \'TARIKH\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_sesi_penilaian) as \'SESI\', \r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_tamat)) as \'MASA\',\r\nlokasi as \'LOKASI\',\r\njumlah_keseluruhan as \'JUMLAH PESERTA\'\r\n\r\nFROM\r\nusr_isac.pro_sesi\r\n\r\nWHERE \r\nkod_kategori_peserta=\'02\' \r\nand kod_iac = \'\".$iac.\"\' \r\nand year(tarikh_sesi)=\'\".$tahun.\"\'\r\nand month(tarikh_sesi)=\'\".$bulan.\"\'\r\n\r\nORDER BY\r\ntarikh_sesi ASC,kod_sesi_penilaian ASC\";\r\n\r\n$sqlSenaraiJadualRs = $myQuery->query($sqlSenaraiJadual,\'SELECT\',\'NAME\');\r\n$countRowJadual = count($sqlSenaraiJadualRs);\r\n\r\n$sqlTarikh = \"SELECT\r\nmin(date_format(tarikh_sesi,\'%d/%m/%Y\')) as \'TARIKH DARI\',\r\nmax(date_format(tarikh_sesi,\'%d/%m/%Y\')) as \'TARIKH HINGGA\',\r\ndate_add(min(date_format(tarikh_sesi,\'%Y/%m/%d\')), interval -7 day) as \'TARIKH EMEL\'\r\nFROM\r\nusr_isac.pro_sesi\r\n\r\nWHERE \r\nkod_kategori_peserta=\'02\' \r\nand kod_iac = \'\".$iac.\"\' \r\nand year(tarikh_sesi)=\'\".$tahun.\"\'\r\nand month(tarikh_sesi)=\'\".$bulan.\"\'\";\r\n\r\n$sqlTarikhRs = $myQuery->query($sqlTarikh,\'SELECT\',\'NAME\');\r\n$tarikh_dari = $sqlTarikhRs[0][\'TARIKH DARI\'];\r\n$tarikh_hingga = $sqlTarikhRs[0][\'TARIKH HINGGA\'];\r\n$tarikh_emel = $sqlTarikhRs[0][\'TARIKH EMEL\'];\r\n\r\n$sqlEmail = \"SELECT description1 as \'EMEL_PENYELIA_KAMPUS\', description2 as \'EMEL_PENGARAH_KAMPUS\' FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'EMAIL_IAC\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=\'\".$iac.\"\'\";\r\n$sqlEmailRs = $myQuery->query($sqlEmail,\'SELECT\',\'NAME\');\r\n$emel_penyelia = $sqlEmailRs[0][\'EMEL_PENYELIA_KAMPUS\'];\r\n$emel_pengarah = $sqlEmailRs[0][\'EMEL_PENGARAH_KAMPUS\'];\r\n\r\n$txtMsg=\'<html>\r\n<head>\r\n<style type=\"text/css\">\r\n<!--\r\n.style30 {font-size: 14px;}\r\n.style53 {font-family: Arial, Helvetica, sans-serif; font-size: 16;}\r\n.style54 {font-family: Arial, Helvetica, sans-serif; font-size: 16; font-weight: bold; }\r\n-->\r\n</style>\r\n</head>\r\n<body>\r\n<table width=\"847\" border=\"0\" cellpadding=\"0\" cellspacing=\"3\" class=\"style30\">\r\n  <tr class=\"style30\">\r\n    <td><span class=\"style53\"><br>      \r\n    Dengan hormatnya perkara di atas dirujuk.</span><br></td>\r\n  </tr>\r\n  <tr class=\"style30\">\r\n    <td> \r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p align=\"justify\" class=\"style53\">2. Sukacita dimaklumkan bahawa Penilaian IT Skill Assessment and Certification (ISAC) untuk individu akan diadakan seperti berikut :-</p>    </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\"><table width=\"576\" border=\"0\" align=\"center\">\r\n      <tr>\r\n        <td width=\"163\" class=\"style54\">Tarikh</td>\r\n        <td width=\"13\" class=\"style54\">:</td>\r\n        <td width=\"386\" class=\"style54\">\';\r\n		$txtMsg.=$tarikh_dari;\r\n		$txtMsg.= \' Hingga \';\r\n		$txtMsg.=$tarikh_hingga;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n      <tr>\r\n        <td class=\"style54\">Bilangan Sesi</td>\r\n        <td class=\"style54\">:</td>\r\n        <td class=\"style54\">\';\r\n		$txtMsg.=$countRowJadual;\r\n		$txtMsg.=\'</td>\r\n      </tr>\r\n    </table></td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>3. Berikut merupakan maklumat jadual penialaian :</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n   <tr>\r\n    <td height=\"16\"><table width=\"838\" border=\"1\">\r\n      <tr>\r\n        <td width=\"134\" class=\"style54\">Tarikh</td>\r\n        <td width=\"179\" class=\"style54\">Sesi</td>\r\n        <td width=\"157\" class=\"style54\">Masa</td>\r\n		<td width=\"157\" class=\"style54\">Lokasi</td>\r\n        <td width=\"144\" class=\"style54\">Jumlah Peserta</td>\r\n      </tr>\';\r\n      \r\n      for($i=0; $i<$countRowJadual; $i++)\r\n	  {\r\n	  $txtMsg.=\'<tr> <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'TARIKH\'];\r\n	  $txtMsg.=\'</td>\r\n        <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'SESI\'];\r\n	  $txtMsg.=\'</td>\r\n        <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'MASA\'];\r\n	  $txtMsg.=\'</td>\r\n        <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'LOKASI\'];\r\n	  $txtMsg.=\'</td>\r\n	  <td>\';\r\n	  $txtMsg.=$sqlSenaraiJadualRs[$i][\'JUMLAH PESERTA\'];\r\n	  $txtMsg.=\'</td>\r\n      </tr>\';\r\n      }\r\n      $txtMsg.=\'\r\n    </table></td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\"> </td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p>4. Sehubungan dengan itu, sukacita sekiranya pihak tuan dapat memastikan pelayan ISAC di IAC anda berjalan dengan baik untuk tujuan tersebut di atas.</p></td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"> </td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\" class=\"style53\">5. Kerjasama daripada pihak tuan dalam perkara di atas didahului dengan ucapan ribuan terima kasih.</td>\r\n  </tr>\r\n  <tr>\r\n    <td height=\"16\" class=\"style53\"><p> </p></td>\r\n  </tr>\r\n    <tr>\r\n    <td height=\"16\" class=\"style53\">Sekian.</td>\r\n  </tr>\r\n</table>\r\n</body>\r\n</html>\';\r\n\r\nnotification(\'NOTIFIKASI_FASILITI_PENILAIAN\',\"EMAIL_TO=\'\".$emel_penyelia.\"\'\",\"EMAIL_CC=\'\".$emel_pengarah.\"\'\",\"EMAIL_FROM=\'isacadmin@intanbk.intan.my\'\",\"EMAIL_SUBJECT=\'NOTIS FASILITI SERVER ISAC / MAKMAL\'\",\"EMAIL_MESSAGE=\'\".$txtMsg.\"\'\",\"PRE_PROCESS_SQL=\'(select * from flc_notification_item where notification_item_status = \'Open\')\'\",\"EXECUTION_DATE=\'\".$tarikh_emel.\"\'\",\"PROGRAM_TYPE=\'EMEL\'\", \"REFFERENCE_ID=\'\".$ref_id.\"\'\"); ', '', '', '00', '1', '2010-02-08', '1', '2010-02-08');
INSERT INTO `flc_bl` VALUES ('76', 'DAFTAR_INDIVIDU_PERIBADI_TEMPAT_TUGAS', 'DAFTAR_INDIVIDU_PERIBADI_TEMPAT_TUGAS', '', 'include(\'db.php\');\r\n\r\n//pro_peserta\r\n//$id_peribadi = $_POST[\'input_map_1_1227\'];\r\n//$id_perkhidmatan = $_POST[\'input_map_1_1229\'];\r\n$id_peserta = $_POST[\'input_map_15_1297\'];\r\n$nama_peserta = $_POST[\'input_map_15_4647\'];\r\n//hidden ic\r\n$no_kad_pengenalan = $_POST[\'input_map_15_4643\'];\r\n$no_kad_pengenalan_lain = $_POST[\'input_map_15_5085\'];\r\n\r\n$kod_gelaran = $_POST[\'input_map_15_4646\'];\r\n$tarikh_lahir_hari = $_POST[\'input_map_15_4649\'];\r\n$tarikh_lahir_bulan = $_POST[\'input_map_15_4650\'];\r\n$tarikh_lahir_tahun = $_POST[\'input_map_15_4651\'];\r\n$kod_jantina = $_POST[\'input_map_15_4652\'];\r\n$emel_peserta = $_POST[\'input_map_15_4660\'];\r\n$no_telefon_pejabat = $_POST[\'input_map_15_4661\'];\r\n$no_telefon_bimbit = $_POST[\'input_map_15_4662\'];\r\n//$kod_kategori_peserta = $_POST[\'input_map_3282_4799\'];\r\n\r\n//pro_perkhidmatan\r\n$kod_gelaran_jawatan = $_POST[\'input_map_15_4653\'];\r\n$kod_peringkat = $_POST[\'input_map_15_4654\'];\r\n$kod_klasifikasi_perkhidmatan = $_POST[\'input_map_15_4655\'];\r\n$kod_gred_jawatan = $_POST[\'input_map_15_4656\'];\r\n$kod_taraf_perjawatan = $_POST[\'input_map_15_4657\'];\r\n$kod_jenis_perkhidmatan = $_POST[\'input_map_15_4658\'];\r\n$tarikh_lantikan_hari = $_POST[\'input_map_15_5224\'];\r\n$tarikh_lantikan_bulan = $_POST[\'input_map_15_5225\'];\r\n$tarikh_lantikan_tahun = $_POST[\'input_map_15_5226\'];\r\n\r\n//pro_tempat_tugas\r\n//$id_peserta = $_POST[\'input_map_15_1297\'];\r\n$gelaran_ketua_jabatan = $_POST[\'input_map_15_127\'];\r\n$kod_kementerian = $_POST[\'input_map_15_128\'];\r\n$kod_jabatan = $_POST[\'input_map_15_1026\'];\r\n$bahagian = $_POST[\'input_map_15_1027\'];\r\n$alamat_1 = $_POST[\'input_map_15_837\'];\r\n$alamat_2 = $_POST[\'input_map_15_129\'];\r\n$alamat_3 = $_POST[\'input_map_15_838\'];\r\n$poskod = $_POST[\'input_map_15_126\'];\r\n$bandar = $_POST[\'input_map_15_130\'];\r\n$kod_negeri = $_POST[\'input_map_15_131\'];\r\n$kod_negara = $_POST[\'input_map_15_132\'];\r\n$nama_penyelia = $_POST[\'input_map_8554_133\'];\r\n$emel_penyelia = $_POST[\'input_map_8554_134\'];\r\n$no_telefon_penyelia = $_POST[\'input_map_8554_135\'];\r\n$no_fax_penyelia = $_POST[\'input_map_8554_136\'];\r\n\r\n//prs permohonan\r\n$tahap = $_POST[\'input_map_15_4644\'];\r\n$tujuan = $_POST[\'input_map_15_4645\'];\r\n//$pilihan_sesi = $_POST[\'input_map_3284_4638\'];\r\n//$kod_status_permohonan = $_POST[\'input_map_3282_8662\'];\r\n\r\n$date1=mktime(date(\"H\")+8,date(\"i\"),date(\"s\"),date(\"m\"),date(\"d\"),date(\"Y\"));\r\n$masa=date(\"H:i:s\",$date1);\r\n$tarikh=date(\"Y-m-d\",$date1);\r\n\r\n//prs_permohonan_peserta\r\n$id_permohonan = $_POST[\'input_map_3282_4749\'];\r\n//$kod_status= $_POST[\'input_map_3284_4919\'];\r\n\r\nif ({SESSION|myKod} == \'1\')\r\n{\r\n$noKP=\'{SESSION|publicID}\';\r\n$noOther=null;\r\n}\r\nelse\r\n{\r\n$noKP=null;\r\n$noOther=\'{SESSION|publicID}\';\r\n}\r\n$sql = \"insert into usr_isac.pro_peserta\r\n(kod_gelaran,nama_peserta,tarikh_lahir,kod_jantina,emel_peserta,kod_kategori_peserta,no_kad_pengenalan,no_kad_pengenalan_lain,no_telefon_pejabat,no_telefon_bimbit)\r\nvalues \r\n(\'$kod_gelaran\',\'$nama_peserta\',str_to_date(\'$tarikh_lahir_hari-$tarikh_lahir_bulan-$tarikh_lahir_tahun\',\'%d-%m-%YY\'),\'$kod_jantina\',\'$emel_peserta\',\'$kod_kategori_peserta\',\'$noKP\',\'$noOther\',\'$no_telefon_pejabat\',\'$no_telefon_bimbit\')\";\r\n\r\n$result = $myQuery->query($sql,\'RUN\');\r\n\r\n$sql2 = \"insert into usr_isac.pro_perkhidmatan (kod_gelaran_jawatan,kod_peringkat,kod_klasifikasi_perkhidmatan,kod_gred_jawatan,kod_taraf_perjawatan,kod_jenis_perkhidmatan,tarikh_lantikan,id_peserta) \r\nvalues \r\n(\'$kod_gelaran_jawatan\',\'$kod_peringkat\',\'$kod_klasifikasi_perkhidmatan\',\'$kod_gred_jawatan\',\'$kod_taraf_perjawatan\',\'$kod_jenis_perkhidmatan\',str_to_date(\'$tarikh_lantikan_hari-$tarikh_lantikan_bulan-$tarikh_lantikan_tahun\',\'%d-%m-%YY\'),\'$id_peserta\')\";\r\n\r\n$result2 = $myQuery->query($sql2,\'RUN\');\r\n\r\n$sql3 = \"insert into usr_isac.pro_tempat_tugas\r\n(id_peserta,gelaran_ketua_jabatan,kod_kementerian,kod_jabatan,bahagian,alamat_1,alamat_2,alamat_3,poskod,bandar,kod_negeri,kod_negara,nama_penyelia,emel_penyelia,no_telefon_penyelia,no_fax_penyelia)\r\nvalues \r\n(\'$id_peserta\',\'$gelaran_ketua_jabatan\',\'$kod_kementerian\',\'$kod_jabatan\',\'$bahagian\',\'$alamat_1\',\'$alamat_2\',\'$alamat_3\',\'$poskod\',\'$bandar\',\'$kod_negeri\',\'$kod_negara\',\'$nama_penyelia\',\'$emel_penyelia\',\'$no_telefon_penyelia\',\'$no_fax_penyelia\')\";\r\n\r\n$result3 = $myQuery->query($sql3,\'RUN\');\r\n\r\n$sql4 = \"insert into usr_isac.prs_permohonan\r\n(id_peserta,kod_tahap,kod_tujuan)\r\nvalues \r\n(\'$id_peserta\',\'$tahap\',\'$tujuan\')\";\r\n\r\n$result4 = $myQuery->query($sql4,\'RUN\');\r\n\r\nif($result && $result2 && $result3 && $result4)\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Berjaya Disimpan</div>\";\r\nredirect(\'index.php?page=page_wrapper&menuID=34&tahap_penilaian=\'.$tahap);\r\n}\r\nelse\r\n{\r\necho \"<div width=\'70%\' id=\'userNotification\'>Maklumat Gagal Disimpan</div>\";\r\n}', '', '', '00', '1', '2010-02-23', '1', '2010-02-23');
