INSERT INTO `flc_page_component` VALUES ('63', 'Sesi Penilaian', '1', '29', 'report', null, 'SELECT distinct\r\ndate_format(a.tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\n\r\na.lokasi AS \'Lokasi Penilaian\'\r\n\r\nFROM usr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_penyelaras c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_penyelaras=c.id_penyelaras) \r\nAND c.log_id_pengguna= \'{SESSION|publicID}\'\r\nORDER BY a.tarikh_sesi ASC,a.kod_sesi_penilaian ASC', '5', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('64', 'Maklumat Penyelaras', '3', '29', 'query', null, 'SELECT \r\nb.nama_penyelaras as \'Nama Penyelaras :\',\r\nb.emel_penyelaras as \'Emel Penyelaras :\',\r\n\r\n/*kementerian*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KEMENTERIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_kementerian) AS \'Kementerian/Agensi :\',\r\n\r\n/*jabatan*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'JABATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_jabatan) AS \'Jabatan :\',\r\n\r\n/*bahagian*/\r\na.bahagian AS \'Bahagian :\',\r\n\r\na.alamat_1 AS \'Alamat Pejabat 1 :\',\r\na.alamat_2 AS \'Alamat Pejabat 2 :\',\r\na.alamat_3 AS \'Alamat Pejabat 3 :\',\r\na.poskod AS \'Poskod :\',\r\na.bandar AS \'Bandar :\',\r\n\r\n/*negeri*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'NEGERI\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_negeri) AS \'Negeri :\',\r\n\r\n/*negara*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'NEGARA\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_negara) AS \'Negara :\',\r\n\r\nb.no_telefon_pejabat as \'No. Telefon Pejabat :\',\r\nb.no_fax as \'No. Fax :\'\r\n\r\nFROM \r\n  usr_isac.pro_tempat_tugas a,\r\n  usr_isac.pro_penyelaras b\r\n\r\nWHERE\r\n(a.id_penyelaras=b.id_penyelaras)\r\nAND\r\n(b.log_id_pengguna= \'{SESSION|publicID}\')', null, '1', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', null);
