INSERT INTO `flc_page_component` VALUES ('101', 'Senarai Jadual Penilaian', '6', '42', 'report', null, 'SELECT\r\ndate_format(tarikh_sesi,\'%d/%m/%Y\'),\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_sesi_penilaian),\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_tamat)),\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_iac),lokasi,\r\njumlah_keseluruhan,\r\nconcat(\'<a href=index.php?page=page_wrapper&menuID=6295&keyid=\',id_sesi,\'>\',((jumlah_keseluruhan)-(bilangan_peserta)),\'</a>\'),\r\nbilangan_peserta\r\n\r\nFROM\r\nusr_isac.pro_sesi\r\n\r\nwhere\r\nkod_kategori_peserta = \'02\'\r\nand\r\nkod_kementerian = if(\'{POST|input_map_96_792}\'=\'\',kod_kementerian,\'{POST|input_map_96_792}\')\r\nand\r\nkod_sesi_penilaian = if(\'{POST|input_map_96_786}\'=\'\',kod_sesi_penilaian,\'{POST|input_map_96_786}\')\r\nand\r\nkod_iac = if(\'{POST|input_map_96_789}\'=\'\',kod_iac,\'{POST|input_map_96_789}\')\r\nand\r\nlokasi LIKE if(\'{POST|input_map_96_788}\'=\'\',lokasi,\'%{POST|input_map_96_788}%\')\r\nand\r\nkod_tahap = if(\'{POST|input_map_96_4913}\'=\'\',kod_tahap,\'{POST|input_map_96_4913}\')\r\nand tarikh_sesi between\r\nif(\'{POST|input_map_96_787}\'=\'\',tarikh_sesi,str_to_date(\'{POST|input_map_96_787}\',\'%d-%m-%Y\'))\r\nand\r\nif(\'{POST|input_map_96_794}\'=\'\',tarikh_sesi,str_to_date(\'{POST|input_map_96_794}\',\'%d-%m-%Y\'))\r\nand\r\nmonth(tarikh_sesi) = if(\'{GET|keyid1}\'=\'\',month(tarikh_sesi),\'{GET|keyid1}\')\r\n\r\nORDER BY\r\ntarikh_sesi ASC,kod_sesi_penilaian ASC', '20', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
