INSERT INTO `flc_page_component` VALUES ('103', 'Senarai Peserta Penilaian', '5', '45', 'report', null, 'SELECT \r\nc.nama_peserta AS \'Nama\',\r\nif(c.no_kad_pengenalan,c.no_kad_pengenalan,c.no_kad_pengenalan_lain) AS \'No. ID\',\r\ndate_format(tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \"Sesi\", (SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Pusat Penilaian\'\r\nFROM \r\nusr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_peserta c\r\nwhere\r\na.id_sesi = b.id_sesi\r\nand b.id_peserta = c.id_peserta\r\nand c.nama_peserta LIKE if (\'{POST|input_map_107_9165}\'=\'\',c.nama_peserta,\'%{POST|input_map_107_9165}%\')\r\nand c.no_kad_pengenalan LIKE if(\'{POST|input_map_107_827}\'=\'\',c.no_kad_pengenalan,\'%{POST|input_map_107_827}%\')\r\nand a.kod_sesi_penilaian = if(\'{POST|input_map_107_5006}\'=\'\',a.kod_sesi_penilaian,\'{POST|input_map_107_5006}\')\r\nand a.tarikh_sesi between (\r\nif(\'{POST|input_map_107_826}\'=\'\',a.tarikh_sesi,str_to_date(\'{POST|input_map_107_826}\',\'%d-%m-%Y\')) \r\nand if(\'{POST|input_map_107_820}\'=\'\',a.tarikh_sesi,str_to_date(\'{POST|input_map_107_820}\',\'%d-%m-%Y\')))\r\nand a.kod_iac = if(\'{POST|input_map_107_821}\'=\'\',a.kod_iac,\'{POST|input_map_107_821}\')\r\nand c.kod_kategori_peserta=\'01\'\r\n\r\nORDER BY\r\nc.nama_peserta ASC', '10', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('104', 'Sesi Penilaian', '6', '4', 'report', null, 'SELECT DISTINCT\r\ndate_format(a.tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Lokasi Penilaian\',\r\n\r\nconcat(\'<a href=\"index.php?page=page_wrapper&menuID=3208&sesi=\',a.id_sesi,\'&tahap_penilaian=\',b.kod_tahap,\'\"\',if(curdate() >= subdate(a.tarikh_sesi,d.TEMPOH_TUTUP_TARIKH_PENILAIAN_INDIVIDU),\'onclick=\"alert(\'\'Tempoh untuk membuat penjadualan semula telah tamat\'\');return false;\"\',\'\'),\'>Penjadualan Semula</a>\') as \"Penjadualan Semula\",\r\nconcat(\'<a href=\"index.php?page=page_wrapper&menuID=104&sesi=\',a.id_sesi,\'&tahap_penilaian=\',b.kod_tahap,\'\"\',if(curdate() >= subdate(a.tarikh_sesi,d.TEMPOH_TUTUP_TARIKH_PENILAIAN_INDIVIDU),\'onclick=\"alert(\'\'Tempoh untuk batal jadual telah tamat\'\');return false;\"\',\'\'),\'>Batal</a>\') as \"Batal\"\r\n\r\nFROM usr_isac.pro_sesi a,usr_isac.prs_permohonan b,usr_isac.pro_peserta c,usr_isac.pro_kawalan_sistem d\r\nWHERE \r\na.id_sesi= b.id_sesi \r\nAND b.id_peserta=c.id_peserta AND d.id_kawalan_sistem=\'1\'\r\nAND (c.no_kad_pengenalan= \'{SESSION|publicID}\' or c.no_kad_pengenalan_lain= \'{SESSION|publicID}\')\r\n', '1', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
