INSERT INTO `flc_page_component` VALUES ('3316', 'Maklumat Perkhidmatan', '5', '22', 'query', null, 'SELECT\r\nb.kod_gelaran_jawatan AS \'Gelaran Jawatan :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'PERINGKAT\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_peringkat) AS \'Peringkat :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KLASIFIKASI_PERKHIDMATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_klasifikasi_perkhidmatan ) AS \'Klasifikasi Perkhidmatan :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'GRED_JAWATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_gred_jawatan) AS \'Gred Jawatan :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'TARAF_PERJAWATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_taraf_perjawatan) AS \'Taraf perjawatan :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'JENIS_PERKHIDMATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_jenis_perkhidmatan) AS \'Jenis Perkhidmatan :\',\r\ndate_format(b.tarikh_lantikan,\'%d-%m-%Y\') AS \'Tarikh Lantikan :\'\r\n\r\nFROM  \r\nusr_isac.pro_peserta a JOIN\r\nusr_isac.pro_perkhidmatan b ON a.id_peserta=b.id_peserta \r\nWHERE \r\na.no_kad_pengenalan= \'{SESSION|publicID}\' or a.no_kad_pengenalan_lain= \'{SESSION|publicID}\'', null, '1', '', '', '', '', 'table', 'USR_ISAC.PRO_PERKHIDMATAN', null, '', null, null, '1', null, null, null, null, null, '', '1');
