INSERT INTO `flc_page_component` VALUES ('203', 'Senarai Peserta Blacklist', '5', '87', 'report', null, 'SELECT \r\nc.nama_peserta AS \'Nama\',\r\n(c.no_kad_pengenalan or c.no_kad_pengenalan_lain)AS \'No. ID\',\r\ndate_format(a.tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Pusat Penilaian\'\r\n\r\nFROM \r\nusr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_peserta c,\r\nusr_isac.prs_permohonan_peserta d\r\nwhere\r\na.id_sesi = b.id_sesi\r\nand b.id_peserta = c.id_peserta\r\nand c.id_peserta = d.id_peserta\r\nand b.kod_status_kehadiran = \'02\'\r\nand \r\nc.nama_peserta LIKE if (\'{POST|input_map_204_9166}\'=\'\',c.nama_peserta,\'%{POST|input_map_204_9166}%\')\r\nand\r\n(c.no_kad_pengenalan = if(\'{POST|input_map_204_1169}\'=\'\',c.no_kad_pengenalan,\'{POST|input_map_204_1169}\')\r\nor c.no_kad_pengenalan_lain = if(\'{POST|input_map_204_1169}\'=\'\',c.no_kad_pengenalan_lain,\'{POST|input_map_204_1169}\'))\r\nand\r\na.kod_sesi_penilaian = if(\'{POST|input_map_204_5007}\'=\'\',a.kod_sesi_penilaian,\'{POST|input_map_204_5007}\')\r\nand \r\na.tarikh_sesi < curdate()\r\nand \r\nd.kod_status = if(\'{POST|input_map_204_1170}\'=\'\',d.kod_status,\'{POST|input_map_204_1170}\')\r\nORDER BY\r\nc.nama_peserta ASC', '10', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
