INSERT INTO `flc_page_component` VALUES ('209', 'Senarai Keputusan', '5', '91', 'report', null, 'select\r\ndate_format(b.tarikh_sesi,\'%d-%m-%Y\') as \'Tarikh Penilaian\',\r\nCONCAT(\'<a href=\"index.php?page=page_wrapper&menuID=142&keyid=\',if(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain),\'&keyid2=\',b.id_sesi,\'\"onclick=\',\'\"\',\"window.open(\'custom_isac/keputusan_keseluruhan_cetak_slip.php?keyid=\",if(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain),\"&id_sesi=\",b.id_sesi,\"\',\'Slip\',\'width=1024,height=768,top=0,left=0,scrollbars=yes\')\",\'\"\',\'>\',e.kod_status_kelulusan,\'</a>\') as \'Slip Keputusan\',\r\na.nama_peserta AS \'Nama\',\r\nif(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain)  AS \'No.My Kad/Polis/Tentera/Pasport\'\r\n\r\nfrom\r\nusr_isac.pro_peserta a,\r\nusr_isac.pro_sesi b,\r\nusr_isac.prs_permohonan c,\r\nusr_isac.prs_penilaian d,\r\nusr_isac.prs_penilaian_peserta e\r\n\r\nwhere\r\n\r\na.id_peserta = c.id_peserta \r\nand b.id_sesi = c.id_sesi\r\nand c.id_sesi = d.id_sesi\r\nand d.id_penilaian = e.id_penilaian\r\nand c.id_peserta = e.id_peserta\r\nand a.no_kad_pengenalan = if(\'{POST|input_map_207_1187}\'=\'\',a.no_kad_pengenalan,\'{POST|input_map_207_1187}\')\r\nand b.kod_sesi_penilaian = if(\'{POST|input_map_207_1192}\'=\'\',b.kod_sesi_penilaian,\'{POST|input_map_207_1192}\')\r\nand b.tarikh_sesi=if(\'{POST|input_map_207_1190}\'=\'\',b.tarikh_sesi,str_to_date(\'{POST|input_map_207_1190}\',\'%d-%m-%Y\'))\r\nand b.kod_iac = if(\'{POST|input_map_207_1189}\'=\'\',b.kod_iac,\'{POST|input_map_207_1189}\')', '80', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
