INSERT INTO `flc_page_component` VALUES ('234', 'Senarai Jadual Penilaian', '7', '39', 'report', null, 'SELECT\r\ndate_format(tarikh_sesi,\'%d/%m/%Y\'),\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_sesi_penilaian), \r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_masa_tamat)),(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=kod_iac),\r\njumlah_keseluruhan,\r\nconcat(\'<a href=index.php?page=page_wrapper&menuID=6294&keyid=\',id_sesi,\'>\',((jumlah_keseluruhan)-(bilangan_peserta)),\'</a>\'),\r\nbilangan_peserta\r\n\r\nFROM\r\nusr_isac.pro_sesi\r\n\r\nWHERE \r\nkod_kategori_peserta=\'01\' \r\nand\r\nkod_tahap = if(\'{POST|input_map_97_4910}\'=\'\',kod_tahap,\'{POST|input_map_97_4910}\')\r\nand\r\nkod_sesi_penilaian = if(\'{POST|input_map_97_800}\'=\'\',kod_sesi_penilaian,\'{POST|input_map_97_800}\')\r\nand\r\nkod_iac = if(\'{POST|input_map_97_796}\'=\'\',kod_iac,\'{POST|input_map_97_796}\')\r\nand\r\ntarikh_sesi between\r\nif(\'{POST|input_map_97_801}\'=\'\',tarikh_sesi,str_to_date(\'{POST|input_map_97_801}\',\'%d-%m-%Y\'))\r\nand\r\nif(\'{POST|input_map_97_798}\'=\'\',tarikh_sesi,str_to_date(\'{POST|input_map_97_798}\',\'%d-%m-%Y\'))\r\nand\r\nmonth(tarikh_sesi) = if(\'{GET|keyid