INSERT INTO `flc_page_component` VALUES ('3284', 'Senarai Jadual Penilaian yang Masih Dibuka dan Ada Kekosongan', '6', '3122', 'report', null, 'SELECT \r\ndate_format(a.tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Lokasi Penilaian\'\r\n\r\nFROM \r\nusr_isac.pro_sesi a, \r\nusr_isac.pro_kawalan_sistem b\r\n\r\nWHERE\r\nmonth(a.tarikh_sesi) = if(\'{POST|input_map_40_306}\'=\'\',month(a.tarikh_sesi),\'{POST|input_map_40_306}\')\r\nAND\r\nyear(a.tarikh_sesi) = if(\'{POST|input_map_40_526}\'=\'\',year(a.tarikh_sesi),\'{POST|input_map_40_526}\')\r\n\r\nAND  id_kawalan_sistem=\'1\'\r\nAND (kod_kategori_peserta=\'01\')\r\nAND (adddate(curdate(),tempoh_tutup_tarikh_penilaian_individu) < (a.tarikh_sesi))\r\nAND a.bilangan_peserta > \'0\'\r\nAND a.kod_tahap=\'{POST|input_map_40_4829}\' \r\nORDER BY\r\na.tarikh_sesi ASC,a.kod_sesi_penilaian ASC', '5', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('6518', 'Senarai Pengguna', '3', '6223', 'report', null, 'SELECT \r\na.username AS \'ID Pengguna\',\r\na.name AS \'Nama\',\r\n\r\nCONCAT(\'<a href=\"index.php?page=page_wrapper&menuID=109&keyid=\',a.userid,\'\">Kemaskini</a>\') as \'Kemaskini\',\r\n\r\nCONCAT(\'<a href=\"index.php?page=page_wrapper&menuID=169&keyid=\',a.userid,\'\">Hapus</a>\') as \'Hapus\'\r\n\r\nFROM pruser a,\r\nflc_user_group_mapping b \r\n\r\nWHERE \r\na.userid=b.user_id \r\nand\r\na.userid not in (\'35\',\'36\',\'37\',\'38\')\r\nand\r\nb.group_id not in (\'4\',\'9\')\r\nand\r\na.username like \'%{POST|input_map_6519_9223}%\'\r\norder by a.username', '15', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
