INSERT INTO `flc_page_component` VALUES ('3313', 'Senarai Jadual Penilaian yang Masih Dibuka dan Ada Kekosongan', '4', '3133', 'report', null, 'SELECT \r\ndate_format(a.tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Lokasi Penilaian\'\r\n\r\nFROM \r\nusr_isac.pro_sesi a, \r\nusr_isac.pro_kawalan_sistem b\r\n\r\nWHERE\r\nmonth(a.tarikh_sesi) = if(\'{POST|input_map_3311_5011}\'=\'\',month(a.tarikh_sesi),\'{POST|input_map_3311_5011}\')\r\nAND\r\nyear(a.tarikh_sesi) = if(\'{POST|input_map_3311_5012}\'=\'\',year(a.tarikh_sesi),\'{POST|input_map_3311_5012}\')\r\nAND id_kawalan_sistem=\'1\'\r\nAND (kod_kategori_peserta=\'01\')\r\nAND adddate(curdate(),tempoh_tutup_tarikh_penilaian_individu) < (a.tarikh_sesi)\r\nAND a.bilangan_peserta > \'0\'\r\nAND a.kod_tahap=\'{POST|input_map_3311_5013}\'\r\nAND a.id_sesi !=\'{GET|sesi}\'\r\n\r\nORDER BY\r\na.tarikh_sesi ASC,a.kod_sesi_penilaian ASC', '5', '1', '', '', '', '', 'bl', 'PENJADUALAN_SEMULA_INDIVIDU', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
