INSERT INTO `flc_page_component` VALUES ('3387', 'Perincian Soalan', '5', '3139', 'report', null, 'select a.id_soalan_perincian,a.penyataan_soalan_perincian as \'Soalan\', \r\nb.skema_jawapan as \'Jawapan\', \r\nb.markah as \'Markah\' from usr_isac.pro_soalan_perician a, usr_isac.pro_jawapan b where a.id_soalan_perincian = b.id_soalan_perincian and a.id_soalan = \'{GET|keyid}\'', '5', '0', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
