INSERT INTO `flc_page_component` VALUES ('3323', 'Maklumat Soalan', '1', '3138', 'report', null, 'select\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'TAHAP\') and referencestatuscode=\'00\'  and referencecode= a.KOD_TAHAP_SOALAN) as \'Tahap Soalan\',\r\n\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'BAHAGIAN_SOALAN\') and referencestatuscode=\'00\'  and referencecode= b.KOD_BAHAGIAN_SOALAN) as \'Bahagian Soalan\', \r\n\r\nb.KOD_BENTUK_SOALAN as \'Bentuk Soalan\',\r\na.TARIKH_CIPTA as \'Tarikh Disediakan\',\r\na.TARIKH_KEMASKINI as \'Tarikh Dikemaskini\',\r\n(select username from isac.pruser where userID = a.ID_PENGGUNA) as \'Disediakan Oleh\',\r\n\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'STATUS_PENGESAHAN_SOALAN\') and referencestatuscode=\'00\'  and referencecode= a.KOD_STATUS) as \'Status Soalan\'\r\n\r\nfrom usr_isac.pro_soalan a,\r\nusr_isac.pro_kemahiran b\r\n\r\nwhere\r\na.ID_KEMAHIRAN=b.ID_KEMAHIRAN\r\nand\r\na.kod_tahap_soalan = if(\'{POST|input_map_3327_5143}\'=\'\',a.kod_tahap_soalan,\'{POST|input_map_3327_5143}\')\r\nand\r\nb.kod_bahagian_soalan = if(\'{POST|input_map_3327_5144}\'=\'\',b.kod_bahagian_soalan,\'{POST|input_map_3327_5144}\')\r\nand\r\nb.kod_bentuk_soalan = if(\'{POST|input_map_3327_5145}\'=\'\',b.kod_bentuk_soalan,\'{POST|input_map_3327_5145}\')\r\norder by a.id_soalan', '10', '1', '', '', '', '', 'table', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
