INSERT INTO `flc_page_component` VALUES ('3353', 'Maklumat Soalan', '1', '3151', 'query', null, 'select \r\n\r\n(select DESCRIPTION1 from isac.refgeneral \r\nwhere \r\nmastercode=(select referencecode\r\nfrom isac.refgeneral where mastercode=\'XXX\' \r\nand DESCRIPTION1=\'TAHAP\') \r\nand \r\nreferencestatuscode=\'00\' \r\nand referencecode=a.kod_tahap_soalan) as \'Tahap Soalan : \',\r\n\r\n(select DESCRIPTION1 from isac.refgeneral \r\nwhere \r\nmastercode=(select referencecode\r\nfrom isac.refgeneral where mastercode=\'XXX\' \r\nand DESCRIPTION1=\'SET_SOALAN\') \r\nand \r\nreferencestatuscode=\'00\' \r\nand referencecode=b.kod_set_soalan) as \'Set Soalan : \',\r\n\r\nb.arahan_umum as \'Arahan Umum : \',\r\n(select DESCRIPTION1 from isac.refgeneral \r\nwhere \r\nmastercode=(select referencecode\r\nfrom isac.refgeneral where mastercode=\'XXX\' \r\nand DESCRIPTION1=\'BAHAGIAN_SOALAN\') \r\nand \r\nreferencestatuscode=\'00\' \r\nand referencecode=b.kod_bahagian_soalan) as \'Bahagian Soalan : \',\r\nb.kod_bentuk_soalan as \'Bentuk Soalan : \',\r\na.arahan_soalan as \'Arahan Soalan : \'\r\nfrom usr_isac.pro_soalan a, usr_isac.pro_kemahiran b\r\nwhere \r\na.id_soalan = b.id_soalan\r\nand a.id_soalan = \'{GET|keyid}\'', null, '1', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', '1');
