INSERT INTO `flc_page_component` VALUES ('3376', 'Senarai Peserta', '2', '3160', 'report', null, 'select\r\nif(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain),\r\na.nama_peserta,\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KLASIFIKASI_PERKHIDMATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_klasifikasi_perkhidmatan),\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'GRED_JAWATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_gred_jawatan)\r\n\r\nfrom\r\nusr_isac.pro_peserta a,\r\nusr_isac.pro_perkhidmatan b,\r\nusr_isac.pro_penyelaras c,\r\nusr_isac.pro_sesi d,\r\nusr_isac.prs_permohonan e\r\nwhere\r\na.id_peserta=b.id_peserta\r\nand\r\na.id_penyelaras=c.id_penyelaras\r\nand\r\na.id_peserta=e.id_peserta\r\nand\r\ne.id_sesi=d.id_sesi\r\nand\r\nc.id_penyelaras=e.id_penyelaras\r\nand\r\nd.id_sesi = \'{GET|sesi}\'\r\nand\r\ne.kod_tahap=\'{GET|tahap_penilaian}\'\r\nand\r\nc.log_id_pengguna=\'{SESSION|publicID}\'\r\n\r\nORDER BY a.id_peserta', '10', '1', '', '', '', '', 'bl', 'DAFTAR_KUMPULAN', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('3377', 'Jadual Penilaian', '1', '3160', 'query', null, 'SELECT DISTINCT\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi :\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'Tarikh :\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa :\',\r\n\r\na.lokasi AS \'Lokasi Penilaian :\'\r\n\r\nFROM usr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_penyelaras c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_penyelaras=c.id_penyelaras) \r\nAND a.id_sesi=\'{GET|sesi}\'\r\nORDER BY a.id_sesi', null, '1', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', '1');
INSERT INTO `flc_page_component` VALUES ('3378', 'Senarai Set Soalan', '1', '3161', 'report', null, 'select \r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'SET_SOALAN\') and referencestatuscode=\'00\' and referencecode=a.kod_set_soalan ) as \"Set Soalan\",\r\n\r\nconcat((select count(kod_set_soalan) from usr_isac.pro_kemahiran b, usr_isac.pro_soalan c  where b.id_kemahiran=c.id_kemahiran and kod_bahagian_soalan = \'01\' and kod_set_soalan = a.kod_set_soalan),\' soalan\') as \"Bahagian A\",\r\n\r\nconcat((select count(kod_set_soalan) from usr_isac.pro_kemahiran b, usr_isac.pro_soalan c  where b.id_kemahiran=c.id_kemahiran and kod_bahagian_soalan = \'02\' and kod_set_soalan = a.kod_set_soalan),\' soalan\') as \"Bahagian B1\",\r\n\r\nconcat((select count(kod_set_soalan) from usr_isac.pro_kemahiran b, usr_isac.pro_soalan c  where b.id_kemahiran=c.id_kemahiran and kod_bahagian_soalan = \'03\' and kod_set_soalan = a.kod_set_soalan),\' soalan\') as  \"Bahagian B2\",\r\n\r\nconcat((select count(kod_set_soalan) from usr_isac.pro_kemahiran b, usr_isac.pro_soalan c  where b.id_kemahiran=c.id_kemahiran and kod_bahagian_soalan = \'04\' and kod_set_soalan = a.kod_set_soalan),\' soalan\') as \"Bahagian C\"\r\n\r\nfrom usr_isac.pro_kemahiran a\r\n\r\nwhere a.kod_set_soalan != \"\" \r\n\r\ngroup by a.kod_set_soalan', '6', '1', '', '', '', '', 'query', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('3379', 'Senarai Soalan Bahagian A', '1', '3162', 'report', null, 'select distinct a.id_kemahiran as \'Id Kemahiran\',\r\n\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'Tahap\') and referencestatuscode=\'00\'  and referencecode= a.KOD_TAHAP_SOALAN) as \'Tahap Soalan\',\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'BAHAGIAN_SOALAN\') and referencestatuscode=\'00\'  and referencecode= b.KOD_BAHAGIAN_SOALAN) as \'Bahagian Soalan\', \r\nb.KOD_BENTUK_SOALAN as \'Bentuk Soalan\',\r\na.TARIKH_CIPTA as \'Tarikh Disediakan\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'STATUS_PENGESAHAN_SOALAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.KOD_STATUS) as \'Status\'\r\n\r\n\r\nfrom usr_isac.pro_soalan a,\r\nusr_isac.pro_kemahiran b\r\n\r\nwhere \r\na.id_kemahiran = b.id_kemahiran\r\nand\r\nb.kod_bahagian_soalan = \'01\' and b.kod_set_soalan = \'{GET|keyid}\'\r\ngroup by a.id_kemahiran\r\norder by b.id_kemahiran', '5', '1', '', '', '', '', 'query', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('3385', 'Senarai Soalan Perincian', '3', '3137', 'report', null, 'select a.penyataan_soalan_perincian as \'Soalan\', \r\nb.skema_jawapan as \'Jawapan\', \r\nb.markah as \'Markah\' ,\r\nb.id_soalan_perincian\r\nfrom usr_isac.pro_soalan_perincian a, usr_isac.pro_jawapan b where a.id_soalan_perincian = b.id_soalan_perincian and a.id_soalan = \'{GET|keyid}\'\r\norder by a.id_soalan asc', '5', '0', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('3380', 'Senarai Peserta', '3', '3163', 'report', null, 'select\r\nif(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain),\r\na.nama_peserta,\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KLASIFIKASI_PERKHIDMATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_klasifikasi_perkhidmatan),\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'GRED_JAWATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_gred_jawatan),\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'TUJUAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=e.kod_tujuan),\r\nconcat(\'<a href=\"index.php?page=page_wrapper&menuID=3218&keyid=\',a.id_peserta,\'&tahap_penilaian=\',e.kod_tahap,\'&sesi=\',d.id_sesi,\'\">Kemaskini</a>\') as \"Kemaskini\",\r\nconcat(\'<a href=\"index.php?page=page_wrapper&menuID=3219&keyid=\',a.id_peserta,\'&tahap_penilaian=\',e.kod_tahap,\'&sesi=\',d.id_sesi,\'\"\',if(curdate() >= subdate(d.tarikh_sesi,f.TEMPOH_TUTUP_TARIKH_PENILAIAN_KUMPULAN),\'onclick=\"alert(\'\'Tarikh untuk batal peserta telah tamat\'\');return false;\"\',\'\'),\'>Batal</a>\') as \"Batal\"\r\n\r\nfrom\r\nusr_isac.pro_peserta a,\r\nusr_isac.pro_perkhidmatan b,\r\nusr_isac.pro_penyelaras c,\r\nusr_isac.pro_sesi d,\r\nusr_isac.prs_permohonan e,\r\nusr_isac.pro_kawalan_sistem f\r\nwhere\r\na.id_peserta=b.id_peserta\r\nand\r\na.id_penyelaras=c.id_penyelaras\r\nand\r\na.id_peserta=e.id_peserta\r\nand\r\ne.id_sesi=d.id_sesi\r\nand\r\nc.id_penyelaras=e.id_penyelaras\r\nand\r\nf.id_kawalan_sistem=\'1\'\r\nand\r\nd.id_sesi = \'{GET|sesi}\'\r\nand\r\ne.kod_tahap=\'{GET|tahap_penilaian}\'\r\nand\r\nc.log_id_p