INSERT INTO `flc_page_component` VALUES ('3381', 'Jadual Penilaian', '1', '3163', 'query', null, 'SELECT DISTINCT\r\na.id_sesi,\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi :\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'Tarikh :\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa :\',\r\n\r\na.lokasi AS \'Lokasi Penilaian :\'\r\n\r\nFROM usr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_penyelaras c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_penyelaras=c.id_penyelaras) \r\nAND a.id_sesi=\'{GET|sesi}\'\r\nORDER BY a.id_sesi', null, '0', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', '1');
INSERT INTO `flc_page_component` VALUES ('3382', 'Senarai Soalan Bahagian B1', '2', '3162', 'report', null, 'select distinct a.id_kemahiran as \'Id Kemahiran\',\r\n\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'Tahap\') and referencestatuscode=\'00\'  and referencecode= a.KOD_TAHAP_SOALAN) as \'Tahap Soalan\',\r\n(select DESCRIPTION1 from refgeneral \r\nwhere mastercode=(select referencecode\r\n from refgeneral where mastercode=\'XXX\' \r\n and description1=\'BAHAGIAN_SOALAN\') and referencestatuscode=\'00\'  and referencecode= b.KOD_BAHAGIAN_SOALAN) as \'Bahagian Soalan\', \r\nb.KOD_BENTUK_SOALAN as \'Bentuk Soalan\',\r\na.TARIKH_CIPTA as \'Tarikh Disediakan\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'STATUS_PENGESAHAN_SOALAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.KOD_STATUS) as \'Status\'\r\n\r\n\r\nfrom usr_isac.pro_soalan a,\r\nusr_isac.pro_kemahiran b\r\n\r\nwhere \r\na.id_kemahiran = b.id_kemahiran\r\nand\r\nb.kod_bahagian_soalan = \'02\' and b.kod_set_soalan = \'{GET|keyid}\'\r\ngroup by a.id_kemahiran\r\norder by b.id_kemahiran', '5', '1', '', '', '', '', 'query', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
