INSERT INTO `flc_page_component` VALUES ('6474', 'Maklumat Soalan ', '3', '6206', 'report', null, 'select\r\na.id_soalan as \'id\',\r\n(select DESCRIPTION1 from refgeneral \r\nwhere \r\nmastercode=(select referencecode\r\nfrom refgeneral where mastercode=\'XXX\' \r\nand DESCRIPTION1=\'TAHAP\') \r\nand \r\nreferencestatuscode=\'00\' \r\nand referencecode=a.KOD_TAHAP_SOALAN),\r\n(select DESCRIPTION1 from refgeneral \r\nwhere \r\nmastercode=(select referencecode\r\nfrom refgeneral where mastercode=\'XXX\' \r\nand DESCRIPTION1=\'KATEGORI_SOALAN\') \r\nand \r\nreferencestatuscode=\'00\'  \r\nand referencecode=a.KOD_KATEGORI_SOALAN),\r\n\r\n(select DESCRIPTION1 from refgeneral \r\nwhere \r\nmastercode=(select referencecode\r\nfrom refgeneral where mastercode=\'XXX\' \r\nand DESCRIPTION1=\'JENIS_SOALAN\') \r\nand \r\nreferencestatuscode=\'00\' \r\nand referencecode=b.KOD_JENIS_SOALAN),\r\na.TARIKH_CIPTA,\r\na.TARIKH_KEMASKINI,\r\n(select username from pruser where userID = a.ID_PENGGUNA)\r\n\r\nfrom usr_isac.pro_soalan a,usr_isac.pro_pengetahuan b\r\nwhere\r\na.ID_SOALAN=b.ID_SOALAN\r\nand\r\na.kod_tahap_soalan=if(\'{POST|input_map_3289_4860}\'=\'\',a.kod_tahap_soalan,\'{POST|input_map_3289_4860}\')\r\nand\r\na.kod_kategori_soalan=if(\'{POST|input_map_3289_4859}\'=\'\',a.kod_kategori_soalan,\'{POST|input_map_3289_4859}\')\r\nand\r\nb.kod_jenis_soalan=if(\'{POST|input_map_3289_4861}\'=\'\',b.kod_jenis_soalan,\'{POST|input_map_3289_4861}\')\r\nand\r\na.kod_status=if(\'{POST|input_map_3289_8945}\'=\'\',a.kod_status,\'{POST|input_map_3289_8945}\')\r\n\r\norder by a.id_soalan', '15', '1', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
