INSERT INTO `flc_page_component` VALUES ('6467', 'Sesi Penilaian', '6', '6203', 'report', null, 'SELECT DISTINCT\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'Tarikh\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Pusat Penilaian\',\r\nconcat(\'<a href=\"index.php?page=page_wrapper&menuID=3208&sesi=\',a.id_sesi,\'&tahap_penilaian=\',b.kod_tahap,\'\"\',if(curdate() >= subdate(a.tarikh_sesi,d.TEMPOH_TUTUP_TARIKH_PENILAIAN_INDIVIDU),\'onclick=\"alert(\'\'Tempoh untuk membuat penjadualan semula telah tamat\'\');return false;\"\',\'\'),\'>Penjadualan Semula</a>\') as \"Penjadualan Semula\",\r\nconcat(\'<a href=\"index.php?page=page_wrapper&menuID=104&sesi=\',a.id_sesi,\'&tahap_penilaian=\',b.kod_tahap,\'\"\',if(curdate() >= subdate(a.tarikh_sesi,d.TEMPOH_TUTUP_TARIKH_PENILAIAN_INDIVIDU),\'onclick=\"alert(\'\'Tempoh untuk batal jadual telah tamat\'\');return false;\"\',\'\'),\'>Batal</a>\') as \"Batal\"\r\n\r\nFROM usr_isac.pro_sesi a,usr_isac.prs_permohonan b,usr_isac.pro_peserta c,usr_isac.pro_kawalan_sistem d\r\nWHERE \r\na.id_sesi= b.id_sesi \r\nAND b.id_peserta=c.id_peserta AND d.id_kawalan_sistem=\'1\'\r\nAND (c.no_kad_pengenalan= \'{SESSION|publicID}\' or c.no_kad_pengenalan_lain= \'{SESSION|publicID}\')', '1', '0', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
