INSERT INTO `flc_page_component` VALUES ('6503', 'Senarai Jadual Penilaian yang Masih Dibuka dan Ada Kekosongan', '4', '6218', 'report', null, 'SELECT \r\ndate_format(a.tarikh_sesi,\'%d/%m/%Y\') AS \'Tarikh\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\na.lokasi AS \'Lokasi Penilaian\',\r\n\r\na.bilangan_peserta AS \'Bilangan Kekosongan\'\r\n\r\nFROM \r\nusr_isac.pro_sesi a,\r\nusr_isac.pro_penyelaras b,\r\nusr_isac.pro_kawalan_sistem c\r\n\r\nWHERE\r\na.kod_kementerian=b.kod_kementerian\r\nAND\r\nmonth(a.tarikh_sesi) = if(\'{POST|input_map_6501_9074}\'=\'\',month(a.tarikh_sesi),\'{POST|input_map_6501_9074}\')\r\nAND\r\nyear(a.tarikh_sesi) = if(\'{POST|input_map_6501_9075}\'=\'\',year(a.tarikh_sesi),\'{POST|input_map_6501_9075}\')\r\nAND (a.kod_kategori_peserta=\'02\')\r\nAND c.id_kawalan_sistem=\'1\'\r\nAND adddate(curdate(),c.tempoh_tutup_tarikh_penilaian_kumpulan) < (a.tarikh_sesi)\r\nAND a.bilangan_peserta > \'0\'\r\nAND a.kod_tahap=\'{POST|input_map_6501_9076}\'\r\nAND a.id_sesi !=\'{GET|sesi}\' \r\nAND b.log_id_pengguna=\'{SESSION|publicID}\'\r\n\r\nORDER BY\r\na.tarikh_sesi ASC', '5', '1', '', '', '', '', 'bl', 'PILIHAN_SESI_PENILAIAN_LAIN', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('6504', 'Maklumat Peserta', '5', '6219', 'report', null, 'select\r\nif(a.no_kad_pengenalan,a.no_kad_pengenalan,a.no_kad_pengenalan_lain) AS \'No.MyKad/Polis/Tentera/Pasport\',\r\na.nama_peserta AS \'Nama\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KLASIFIKASI_PERKHIDMATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_klasifikasi_perkhidmatan) AS \'Klasifikasi Perkhidmatan\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'GRED_JAWATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_gred_jawatan) AS \'Gred Jawatan\'\r\n\r\nfrom\r\nusr_isac.pro_peserta a,\r\nusr_isac.pro_perkhidmatan b,\r\nusr_isac.pro_penyelaras c,\r\nusr_isac.pro_sesi d,\r\nusr_isac.prs_permohonan e\r\nwhere\r\na.id_peserta=b.id_peserta\r\nand\r\na.id_penyelaras=c.id_penyelaras\r\nand\r\na.id_peserta=e.id_peserta\r\nand\r\ne.id_sesi=d.id_sesi\r\nand\r\nc.id_penyelaras=e.id_penyelaras\r\nand\r\nc.log_id_pengguna=\'{SESSION|publicID}\'\r\n\r\nORDER BY a.id_peserta', '5', '0', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '1');
INSERT INTO `flc_page_component` VALUES ('6505', 'Sesi Penilaian', '1', '6219', 'report', null, 'SELECT DISTINCT\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'Tarikh\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Pusat Penilaian\',\r\na.lokasi AS \'Lokasi Penilaian\'\r\n\r\nFROM usr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_penyelaras c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_penyelaras=c.id_penyelaras) \r\nAND c.log_id_pengguna= \'{SESSION|publicID}\'\r\nORDER BY a.id_sesi', '5', '0', '', '', '', '', '', '', null, '', null, null, '0', null, '', '', '0', '0', '', '0');
INSERT INTO `flc_page_component` VALUES ('6506', 'Maklumat Penyelaras', '3', '6219', 'query', null, 'SELECT \r\nb.nama_penyelaras as \'Nama Penyelaras :\',\r\nb.emel_penyelaras as \'Emel Penyelaras :\',\r\n\r\n/*kementerian*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KEMENTERIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=b.kod_kementerian) AS \'Kementerian/Agensi :\',\r\n\r\n/*jabatan*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'JABATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_jabatan) AS \'Jabatan :\',\r\n\r\n/*bahagian*/\r\na.bahagian AS \'Bahagian :\',\r\n\r\na.alamat_1 AS \'Alamat Pejabat 1 :\',\r\na.alamat_2 AS \'Alamat Pejabat 2 :\',\r\na.alamat_3 AS \'Alamat Pejabat 3 :\',\r\na.poskod AS \'Poskod :\',\r\na.bandar AS \'Bandar :\',\r\n\r\n/*negeri*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'NEGERI\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_negeri) AS \'Negeri :\',\r\n\r\n/*negara*/\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'NEGARA\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_negara) AS \'Negara :\',\r\n\r\nb.no_telefon_pejabat as \'No. Telefon Pejabat :\',\r\nb.no_fax as \'No. Fax :\'\r\n\r\nFROM \r\n  usr_isac.pro_tempat_tugas a,\r\n  usr_isac.pro_penyelaras b\r\n\r\nWHERE\r\n(a.id_penyelaras=b.id_penyelaras)\r\nAND\r\n(b.log_id_pengguna= \'{SESSION|publicID}\')', null, '1', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', null);
INSERT INTO `flc_page_component` VALUES ('6507', 'Tahap Penilaian', '2', '6219', 'query', null, 'SELECT DISTINCT\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'TAHAP\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_tahap) AS \'Tahap Penilaian :\',\r\n\r\na.kod_tujuan AS \'Tujuan Permohonan :\'\r\n\r\nFROM usr_isac.prs_permohonan a, usr_isac.pro_penyelaras b \r\nWHERE \r\na.id_penyelaras = b.id_penyelaras \r\nAND b.log_id_pengguna= \'{SESSION|publicID}\'', null, '0', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', null);
