INSERT INTO `flc_page_component` VALUES ('8565', 'Maklumat Tempat Bertugas', '6', '8239', 'query', null, 'SELECT \r\na.gelaran_ketua_jabatan AS \'Jawatan Ketua Jabatan :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'KEMENTERIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_kementerian) AS \'Kementerian/Agensi :\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'JABATAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_jabatan) AS \'Jabatan :\',\r\na.bahagian AS \'Bahagian :\',\r\na.alamat_1 AS \'Alamat Pejabat 1 :\',\r\na.alamat_2 AS \'Alamat Pejabat 2 :\',\r\na.alamat_3 AS \'Alamat Pejabat 3 :\',\r\na.poskod AS \'Poskod :\',\r\na.bandar AS \'Bandar :\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'NEGERI\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_negeri) AS \'Negeri :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'NEGARA\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_negara) AS \'Negara :\',\r\n\r\na.nama_penyelia AS \'Nama Penyelia :\',\r\na.emel_penyelia AS \'Emel Penyelia :\',\r\na.no_telefon_penyelia AS \'No. Telefon Penyelia :\',\r\na.no_fax_penyelia AS \'No. Fax Penyelia :\'\r\n\r\nFROM usr_isac.pro_tempat_tugas a,usr_isac.pro_peserta b\r\nWHERE\r\n(a.id_peserta=b.id_peserta) AND\r\na.id_peserta = \'{GET|keyid2}\'', null, '1', '', '', '', '', 'table', 'USR_ISAC.PRO_TEMPAT_TUGAS', null, '', null, null, '1', null, null, null, null, null, '', null);
INSERT INTO `flc_page_component` VALUES ('8566', 'Tujuan Permohonan', '3', '8239', 'query', null, 'SELECT\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'TUJUAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_tujuan) AS \'Tujuan Permohonan :\'\r\n\r\nFROM usr_isac.prs_permohonan a, usr_isac.pro_peserta b \r\nWHERE \r\na.id_peserta = b.id_peserta \r\nAND\r\nb.id_peserta = \'{GET|keyid2}\'', null, '1', 'select', '', '', '', 'table', 'USR_ISAC.PRS_PERMOHONAN', null, '', null, null, '1', null, null, null, null, null, '', null);
INSERT INTO `flc_page_component` VALUES ('8567', 'Sesi Penilaian', '1', '8239', 'query', null, 'SELECT \r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'TAHAP\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_tahap) AS \'Tahap Penilaian :\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=\'XXX\' \r\nAND description1=\'SESI_PENILAIAN\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_sesi_penilaian) AS \'Sesi :\',\r\n\r\ndate_format(a.tarikh_sesi,\'%d-%m-%Y\') AS \'Tarikh :\',\r\n\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_mula\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_mula),\r\n\'-\',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'masa_tamat\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_masa_tamat)) AS \'Masa :\',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=\'XXX\' \r\nAND description1=\'iac\') \r\nAND\r\nreferencestatuscode=\'00\' \r\nAND referencecode=a.kod_iac) AS \'Pusat Penilaian :\'\r\n\r\nFROM usr_isac.pro_sesi a,usr_isac.prs_permohonan b,usr_isac.pro_peserta c\r\nWHERE \r\n(a.id_sesi= b.id_sesi AND b.id_peserta=c.id_peserta) \r\nAND \r\nc.id_peserta=\'{GET|keyid2}\'', null, '0', '', '', '', '', '', '', null, '', null, null, '1', null, null, null, null, null, '', null);
