 $myQuery->query($sql1,''RUN'');\r\n\r\n	//form maklumat penyelaras\r\n	$sqlupdate2 = "update usr_isac.pro_tempat_tugas set\r\n\r\n	id_tempat_tugas =''$id_tempat_tugas'',\r\n	nama_penyelia =''$nama_penyelia'',\r\n	emel_penyelia = ''$emel_penyelia'',\r\n	kod_kementerian = ''$kod_kementerian'',\r\n	bahagian = ''$kod_jabatan'',\r\n	alamat_1 = ''$alamat_1'',\r\n	alamat_2 = ''$alamat_2'',\r\n	alamat_3 = ''$alamat_3'',\r\n	poskod= ''$poskod'',\r\n	bandar = ''$bandar'',\r\n	kod_negeri = ''$kod_negeri'',\r\n	kod_negara = ''$kod_negara'',\r\n	no_telefon_penyelia = ''$no_telefon_penyelia'',\r\n	no_fax_penyelia = ''$no_fax_penyelia'';\r\n\r\n		$result2 = $myQuery->query($sql2,''RUN'');\r\n\r\n	//form maklumat peserta table 1\r\n	/*$sqlupdate3 = "update usr_isac.pro_peserta\r\n	\r\n	no_kad_pengenalan = ''$no_kad_pengenalan'',\r\n	no_kad_pengenalan_lain = ''$no_kad_pengenalan_lain'',	\r\n	nama_peserta = ''$nama_peserta'';\r\n\r\n		$result3 = $myQuery->query($sql3,''RUN'');\r\n	\r\n	//form maklumat peserta table 2\r\n	$sqlupdate4 = "update usr_isac.pro_perkhidmatan\r\n	kod_klasifikasi_perkhidmatan = ''$kod_klasifikasi_perkhidmatan'',\r\n	kod_gred_jawatan = ''$kod_gred_jawatan'';\r\n	\r\n		$result4 = $myQuery->query($sql4,''RUN'');*/\r\n\r\nwhere\r\n\r\nid_permohonan = ''$keyid1''\r\nand\r\nid_tempat_tugas = ''$keyid2''";\r\n\r\nif($result1&&result2){\r\necho "Maklumat Berjaya Dikemaskini";}\r\nelse\r\n{\r\n"Maklumat Gagal Dikemaskini"\r\n}', '', '', '00', '1', '2009-10-27', '1', '2009-10-29'),
(25, 'PEMBATALAN_JADUAL_PENILAIAN', 'PEMBATALAN_JADUAL_PENILAIAN', '', 'include(''db.php'');\r\n\r\n$id_peserta = $_POST[''input_map_119_5161''];\r\n$sebab_pembatalan = $_POST[''input_map_3317_887''];\r\n$date1=mktime(date("H")+8,date("i"),date("s"),date("m"),date("d"),date("Y"));\r\n$masa=date("H:i:s",$date1);\r\n$tarikh=date("Y-m-d",$date1);\r\n\r\n//$sql = "update usr_isac.prs_permohonan set id_sesi='''',sebab_batal=''$sebab_pembatalan'' where id_peserta=''$id_peserta''";\r\n\r\n	$sql = "update usr_isac.prs_permohonan a,usr_isac.pro_peserta b set \r\n	a.id_sesi='''',a.sebab_batal=''$sebab_pembatalan'',tarikh_batal_permohonan=''$tarikh'' where a.id_peserta=b.id_peserta and (b.no_kad_pengenalan=''{SESSION|publicID}'' or b.no_kad_pengenalan_lain=''{SESSION|publicID}'')";\r\n\r\n$result = $myQuery->query($sql,''RUN'');\r\n\r\nif($result)\r\n{\r\necho "<div width=''70%'' id=''userNotification''>Maklumat Berjaya Dikemaskini</div>";\r\nredirect(''index.php?page=page_wrapper&menuID=35'');\r\n}\r\nelse\r\n{\r\necho "<div width=''70%'' id=''userNotification''>Maklumat Gagal Dikemaskini</div>";\r\n\r\n}', '', '', '00', '1', '2009-10-27', '35', '2009-10-28'),
