(3307, 'Tahap Penilaian', 2, 22, 'query', NULL, 'SELECT\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=''XXX'' \r\nAND description1=''TAHAP'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=a.kod_tahap) AS ''Tahap Penilaian :'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=''XXX'' \r\nAND description1=''TUJUAN'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=a.kod_tujuan) AS ''Tujuan Permohonan :''\r\n\r\nFROM usr_isac.prs_permohonan a, usr_isac.pro_peserta b \r\nWHERE \r\na.id_peserta = b.id_peserta \r\nAND (b.no_kad_pengenalan= ''{SESSION|publicID}'' or b.no_kad_pengenalan_lain= ''{SESSION|publicID}'')', NULL, 1, 'select', '', '', '', 'table', 'USR_ISAC.PRS_PERMOHONAN', NULL, '', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', NULL),
(3308, 'Maklumat Peserta', 3, 22, 'query', NULL, 'SELECT\r\nif(no_kad_pengenalan,no_kad_pengenalan,no_kad_pengenalan_lain)AS ''No.MyKad/Polis/Askar/Pasport :'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=''XXX'' \r\nAND description1=''GELARAN'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=kod_gelaran) AS ''Gelaran :'',\r\nnama_peserta AS ''Nama :'',\r\nconcat(tarikh_lahir_hari,''-'',tarikh_lahir_bulan,''-'',tarikh_lahir_tahun) AS ''Tarikh Lahir :'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=''XXX'' \r\nAND description1=''JANTINA'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=kod_jantina) AS ''Jantina'',\r\nemel_peserta AS ''Emel :'',\r\nno_telefon_pejabat AS ''No. Telefon Pejabat :'',\r\nno_telefon_bimbit AS ''No. Telefon Bimbit :''\r\nFROM  \r\nusr_isac.pro_peserta\r\nWHERE \r\nno_kad_pengenalan= ''{SESSION|publicID}'' or no_kad_pengenalan_lain= ''{SESSION|publicID}''', NULL, 1, '', '', '', '', 'table', 'USR_ISAC.PRO_PESERTA', NULL, '', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', NULL),
(3309, 'Maklumat Tempat Bertugas', 5, 22, 'query', NULL, 'SELECT \r\na.gelaran_ketua_jabatan AS ''Gelaran Ketua Jabatan :'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=''XXX'' \r\nAND description1=''KEMENTERIAN'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=a.kod_kementerian) AS ''Kementerian/Agensi :'',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=''XXX'' \r\nAND description1=''JABATAN'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=a.kod_jabatan) AS ''Jabatan :'',\r\na.bahagian AS ''Bahagian :'',\r\na.alamat_1 AS ''Alamat Pejabat 1 :'',\r\na.alamat_2 AS ''Alamat Pejabat 2 :'',\r\na.alamat_3 AS ''Alamat Pejabat 3 :'',\r\na.poskod AS ''Poskod :'',\r\na.bandar AS ''Bandar :'',\r\n\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=''XXX'' \r\nAND description1=''NEGERI'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=a.kod_negeri) AS ''Negeri :'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral where mastercode=''XXX'' \r\nAND description1=''NEGARA'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=a.kod_negara) AS ''Negara :'',\r\n\r\na.nama_penyelia AS ''Nama Penyelia :'',\r\na.emel_penyelia AS ''Emel Penyelia :'',\r\na.no_telefon_penyelia AS ''No. Telefon Penyelia :'',\r\na.no_fax_penyelia AS ''No. Fax Penyelia :''\r\n\r\nFROM usr_isac.pro_tempat_tugas a,usr_isac.pro_peserta b\r\nWHERE\r\n(a.id_peserta=b.id_peserta) AND\r\n(b.no_kad_pengenalan= ''{SESSION|publicID}'' or b.no_kad_pengenalan_lain= ''{SESSION|publicID}'')', NULL, 1, '', '', '', '', 'table', 'USR_ISAC.PRO_TEMPAT_TUGAS', NULL, '', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', NULL),
(3319, 'Soalan Umum', 2, 3139, 'tabular', NULL, NULL, 1, 1, '', '', '', '', '', '', NULL, '', NULL, NULL, 1, NULL, '', '', 0, 0, '', 1),
(3310, 'Maklumat Peserta', 3, 86, 'query', NULL, 'SELECT \r\nc.nama_peserta AS ''Nama'',c.no_kad_pengenalan AS ''No.My Kad/Polis/Askar/Pasport'',date_format(tarikh_sesi,''%d-%m-%Y'')  AS ''Tarikh'',\r\nconcat(\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=''XXX'' \r\nAND description1=''masa_mula'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=kod_masa_mula),\r\n''-'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=''XXX'' \r\nAND description1=''masa_tamat'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=kod_masa_tamat))  AS ''Masa'',\r\n(SELECT description1 FROM refgeneral \r\nWHERE\r\nmastercode=(SELECT referencecode\r\nFROM refgeneral WHERE mastercode=''XXX'' \r\nAND description1=''iac'') \r\nAND\r\nreferencestatuscode=''00'' \r\nAND referencecode=kod_iac) AS ''Pusat Penilaian''\r\nFROM \r\nusr_isac.pro_sesi a,\r\nusr_isac.prs_permohonan b,\r\nusr_isac.pro_peserta c,\r\nusr_isac.prs_penilaian_peserta d\r\nwhere\r\na.id_sesi = b.id_sesi\r\nand b.id_peserta = c.id_peserta\r\nand c.id_peserta  = d.id_peserta\r\nand d.id_peserta = ''{GET|keyid}''', NULL, 1, '', '', '', '', '', '', NULL, '', NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', NULL),
